import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCart, Phone, Menu, X } from 'lucide-react';
import { useCart } from '../context/CartContext';

const Header = () => {
  const { getItemCount } = useCart();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const menuItems = [
    { name: 'Accueil', path: '/' },
    { name: 'Catalogue', path: '/catalogue' },
    { name: 'À Propos', path: '/apropos' },
    { name: 'Contact', path: '/contact' },
  ];

  return (
    <header className="bg-white shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4">
        <div className="flex justify-between items-center">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-2">
            <div className="bg-aloe-green p-2 rounded-lg">
              <span className="text-2xl font-bold text-white">V</span>
            </div>
            <div>
              <h1 className="text-xl font-bold text-aloe-green">Vitalité & Bien-être au Naturel</h1>
              <p className="text-xs text-forever-gray-text"></p>
            </div>
          </Link>
          
          {/* Navigation desktop */}
          <nav className="hidden md:flex space-x-6">
            {menuItems.map((item) => (
              <Link
                key={item.name}
                to={item.path}
                className="nav-link text-forever-gray-dark hover:text-aloe-green"
              >
                {item.name}
              </Link>
            ))}
          </nav>
          
          {/* Actions */}
          <div className="flex items-center space-x-4">
            <a 
              href="https://wa.me/221771234567" 
              target="_blank" 
              rel="noopener noreferrer"
              className="btn-whatsapp hidden md:flex items-center space-x-2 px-4 py-2"
            >
              <Phone size={18} />
              <span>Commander sur WhatsApp</span>
            </a>
            
            <Link 
              to="/chariots" 
              className="relative p-2 text-forever-gray-dark hover:text-aloe-green transition-colors"
            >
              <ShoppingCart size={24} />
              {getItemCount() > 0 && (
                <span className="absolute -top-1 -right-1 bg-aloe-gold text-forever-gray-dark text-xs rounded-full w-5 h-5 flex items-center justify-center font-bold">
                  {getItemCount()}
                </span>
              )}
            </Link>
            
            {/* Bouton menu mobile */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden p-2 text-forever-gray-dark"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Menu mobile */}
        {isMenuOpen && (
          <div className="md:hidden mt-4 pt-4 border-t border-forever-gray-medium">
            <div className="flex flex-col space-y-3">
              {menuItems.map((item) => (
                <Link
                  key={item.name}
                  to={item.path}
                  className="nav-link py-2 text-forever-gray-dark hover:text-aloe-green"
                  onClick={() => setIsMenuOpen(false)}
                >
                  {item.name}
                </Link>
              ))}
              <a 
                href="https://wa.me/777131362" 
                target="_blank" 
                rel="noopener noreferrer"
                className="btn-whatsapp flex items-center justify-center space-x-2 py-3 mt-2"
                onClick={() => setIsMenuOpen(false)}
              >
                <Phone size={18} />
                <span>Commander sur WhatsApp</span>
              </a>
            </div>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
