import React, { useState } from 'react';
import { 
  products, 
  categories, 
  formatPrice 
} from '../../data/products';
import { useCart } from '../../context/CartContext';
import { 
  Search, 
  Filter, 
  ShoppingCart, 
  Star, 
  Heart, 
  Zap,
  Leaf
} from 'lucide-react';

const ProductCard = ({ product, addToCart }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isImageLoaded, setIsImageLoaded] = useState(false);

  const getCategoryColor = (category) => {
    switch(category) {
      case 'Soins & Beauté': return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'Boissons & Bien-être': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'Compléments Alimentaires': return 'bg-orange-100 text-orange-800 border-orange-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <div 
      className="group relative bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-2xl transition-all duration-500 hover:-translate-y-2 border border-gray-100"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Badge de catégorie animé */}
      <div className={`absolute top-4 left-4 z-10 px-3 py-1.5 rounded-full text-xs font-bold border ${getCategoryColor(product.category)} transition-all duration-300 ${isHovered ? 'scale-110' : ''}`}>
        {product.category.split(' & ')[0]}
      </div>

      {/* Badge de stock limité */}
      {product.stock < 10 && product.stock > 0 && (
        <div className="absolute top-4 right-4 z-10 px-3 py-1.5 bg-red-500 text-white rounded-full text-xs font-bold animate-pulse">
          <Zap size={10} className="inline mr-1" />
          Stock limité
        </div>
      )}

      {/* Container d'image avec effet de zoom */}
      <div className="relative h-72 bg-gradient-to-br from-gray-50 to-gray-100 overflow-hidden">
        {/* Image principale */}
        <div className={`absolute inset-0 flex items-center justify-center p-8 transition-opacity duration-500 ${isImageLoaded ? 'opacity-100' : 'opacity-0'}`}>
          <img
            src={product.image}
            alt={product.name}
            className="max-h-56 w-auto object-contain transition-transform duration-700 group-hover:scale-110"
            onLoad={() => setIsImageLoaded(true)}
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = `data:image/svg+xml;base64,${btoa(`
                <svg width="400" height="300" xmlns="http://www.w3.org/2000/svg">
                  <rect width="400" height="300" fill="#f8fafc"/>
                  <text x="200" y="140" font-family="Arial" font-size="16" text-anchor="middle" fill="#94a3b8">
                    ${product.name}
                  </text>
                  <text x="200" y="170" font-family="Arial" font-size="12" text-anchor="middle" fill="#cbd5e1">
                    Forever Living
                  </text>
                  <circle cx="200" cy="220" r="40" fill="#2E7D32" opacity="0.1"/>
                  <path d="M180 210 L220 230 L190 250" fill="none" stroke="#2E7D32" stroke-width="3"/>
                </svg>
              `)}`;
            }}
          />
        </div>

        {/* Skeleton loader */}
        {!isImageLoaded && (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="w-48 h-48 bg-gradient-to-r from-gray-200 to-gray-300 rounded-lg animate-pulse"></div>
          </div>
        )}

        {/* Overlay au survol */}
        <div className={`absolute inset-0 bg-gradient-to-t from-black/10 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500`}></div>
        
        {/* Bouton favori */}
        <button className="absolute top-4 right-4 z-20 p-2 bg-white/90 backdrop-blur-sm rounded-full opacity-0 group-hover:opacity-100 transition-all duration-300 hover:scale-110 hover:bg-red-50">
          <Heart size={18} className="text-gray-600 hover:text-red-500" />
        </button>
      </div>

      {/* Contenu de la carte */}
      <div className="p-6">
        {/* Nom du produit avec effet de soulignement */}
        <h3 className="font-bold text-lg text-gray-800 mb-3 line-clamp-2 min-h-[56px] group-hover:text-aloe-green transition-colors duration-300">
          {product.name}
        </h3>

        {/* Description */}
        <p className="text-sm text-gray-600 mb-4 line-clamp-2 min-h-[40px]">
          {product.description}
        </p>

        {/* Code produit */}
        <div className="flex items-center text-xs text-gray-500 mb-4">
          <span className="bg-gray-100 px-2 py-1 rounded">Code: {product.code}</span>
        </div>

        {/* Prix et action */}
        <div className="flex items-center justify-between pt-4 border-t border-gray-100">
          <div>
            <div className="text-2xl font-bold text-aloe-green">
              {formatPrice(product.price)}
            </div>
            <div className="text-xs text-gray-500">
              {product.stock > 0 ? `${product.stock} disponibles` : 'Rupture de stock'}
            </div>
          </div>

          {/* Bouton d'action avec effet */}
          <button
            onClick={() => addToCart(product)}
            disabled={product.stock === 0}
            className={`relative overflow-hidden px-6 py-3 rounded-xl font-semibold transition-all duration-300 transform hover:scale-105 ${
              product.stock === 0
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                : 'bg-gradient-to-r from-aloe-green to-aloe-green-dark text-white shadow-lg hover:shadow-xl'
            }`}
          >
            {/* Effet de lueur au survol */}
            <div className="absolute -inset-1 bg-gradient-to-r from-aloe-green to-aloe-gold rounded-xl blur opacity-0 group-hover:opacity-30 transition-opacity duration-500"></div>
            
            {/* Contenu du bouton */}
            <div className="relative flex items-center space-x-2">
              <ShoppingCart size={18} />
              <span>{product.stock === 0 ? 'Rupture' : 'Ajouter'}</span>
            </div>

            {/* Effet de remplissage */}
            <div className="absolute inset-0 bg-gradient-to-r from-aloe-green-dark to-aloe-green opacity-0 group-hover:opacity-100 transition-opacity duration-300 -translate-x-full group-hover:translate-x-0"></div>
          </button>
        </div>

        {/* Évaluation (optionnel) */}
        <div className="flex items-center justify-between mt-4 pt-4 border-t border-gray-100">
          <div className="flex items-center">
            {[...Array(5)].map((_, i) => (
              <Star 
                key={i} 
                size={14} 
                className={`${i < 4 ? 'text-aloe-gold fill-aloe-gold' : 'text-gray-300'}`} 
              />
            ))}
            <span className="ml-2 text-xs text-gray-600">4.8</span>
          </div>
          <div className="flex items-center text-xs text-green-600">
            <Leaf size={12} className="mr-1" />
            Produit naturel
          </div>
        </div>
      </div>

      {/* Effet de bordure lumineuse au survol */}
      <div className="absolute inset-0 rounded-2xl border-2 border-transparent group-hover:border-aloe-green/30 transition-all duration-500 pointer-events-none"></div>
    </div>
  );
};

const CataloguePage = () => {
  const [selectedCategory, setSelectedCategory] = useState('Tous les produits');
  const [searchTerm, setSearchTerm] = useState('');
  const { addToCart } = useCart();

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'Tous les produits' || product.category === selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4">
        {/* En-tête */}
        <div className="text-center mb-8">
          <h1 className="text-4xl md:text-5xl font-bold text-aloe-green-dark mb-4">
            Notre Catalogue Premium
          </h1>
          <p className="text-lg text-gray-600">
            Découvrez nos produits Forever Living à base d'Aloe Vera 100% naturel
          </p>
        </div>

        {/* Filtres */}
        <div className="mb-8 bg-white rounded-2xl shadow-lg p-6">
          <div className="flex flex-col md:flex-row gap-4">
            {/* Barre de recherche */}
            <div className="relative flex-1">
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Rechercher un produit (nom, description)..."
                className="w-full pl-12 pr-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-aloe-green focus:border-transparent"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* Filtre par catégorie */}
            <div className="flex items-center gap-3">
              <Filter size={20} className="text-gray-500" />
              <select
                className="border border-gray-300 rounded-xl px-4 py-3 focus:outline-none focus:ring-2 focus:ring-aloe-green focus:border-transparent min-w-[200px]"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {categories.map(category => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Résultats */}
        <div className="mb-6 flex justify-between items-center">
          <h2 className="text-xl font-bold text-gray-800">
            {filteredProducts.length} produit{filteredProducts.length !== 1 ? 's' : ''} trouvé{filteredProducts.length !== 1 ? 's' : ''}
          </h2>
          <div className="text-sm text-gray-600 flex items-center">
            <Leaf size={16} className="mr-2 text-aloe-green" />
            <span>Tous nos produits sont 100% naturels</span>
          </div>
        </div>

        {/* Grille de produits */}
        {filteredProducts.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
            {filteredProducts.map(product => (
              <ProductCard 
                key={product.id} 
                product={product} 
                addToCart={addToCart} 
              />
            ))}
          </div>
        ) : (
          /* État vide */
          <div className="text-center py-16 bg-white rounded-2xl shadow-lg">
            <Search size={64} className="mx-auto text-gray-300 mb-4" />
            <h3 className="text-xl font-semibold text-gray-700 mb-2">
              Aucun produit trouvé
            </h3>
            <p className="text-gray-600 mb-6">
              Essayez de modifier vos critères de recherche
            </p>
            <button
              onClick={() => {
                setSelectedCategory('Tous les produits');
                setSearchTerm('');
              }}
              className="bg-aloe-green text-white px-6 py-3 rounded-xl font-semibold hover:bg-aloe-green-dark transition-colors"
            >
              Voir tous les produits
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CataloguePage;
