import React, { useState } from 'react';
import { products, categories, categoryIcons, formatPrice } from '../../data/products';
import { useCart } from '../../context/CartContext';
import { Search, Filter, ShoppingCart, Star, Package } from 'lucide-react';
import { Link } from 'react-router-dom';

const CataloguePage = () => {
  const [selectedCategory, setSelectedCategory] = useState('Tous les produits');
  const [searchTerm, setSearchTerm] = useState('');
  const { addToCart } = useCart();

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'Tous les produits' || product.category === selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (product.tags && product.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase())));
    return matchesCategory && matchesSearch;
  });

  // Fonction pour crÃ©er une image par dÃ©faut
  const createDefaultImage = (productName, category) => {
    const colors = {
      "Soins & BeautÃ©": { bg: "#9333ea", text: "#f3e8ff" },
      "Boissons & Bien-Ãªtre": { bg: "#3b82f6", text: "#dbeafe" },
      "ComplÃ©ments Alimentaires": { bg: "#f97316", text: "#ffedd5" }
    };
    
    const color = colors[category] || { bg: "#2E7D32", text: "#dcfce7" };
    
    return `data:image/svg+xml;base64,${btoa(`
      <svg width="400" height="300" xmlns="http://www.w3.org/2000/svg">
        <rect width="400" height="300" fill="${color.bg}"/>
        <text x="200" y="120" font-family="Arial, sans-serif" font-size="18" text-anchor="middle" fill="${color.text}">
          ${productName.length > 25 ? productName.substring(0, 25) + '...' : productName}
        </text>
        <text x="200" y="160" font-family="Arial, sans-serif" font-size="14" text-anchor="middle" fill="${color.text}">
          Forever Living
        </text>
        <text x="200" y="200" font-family="Arial, sans-serif" font-size="12" text-anchor="middle" fill="${color.text}80">
          Image en chargement...
        </text>
        <circle cx="200" cy="250" r="30" fill="${color.text}20" stroke="${color.text}80" stroke-width="2"/>
        <text x="200" y="255" font-family="Arial, sans-serif" font-size="16" text-anchor="middle" fill="${color.text}">
          ${categoryIcons[category] || 'ðŸ“¦'}
        </text>
      </svg>
    `)}`;
  };


  
  // Fonction pour gÃ©rer les erreurs d'image
  const handleImageError = (e, product) => {
    e.target.onerror = null;
    e.target.src = createDefaultImage(product.name, product.category);
  };

  // Fonction pour vÃ©rifier si l'image existe
  const checkImageExists = (url) => {
    return new Promise((resolve) => {
      const img = new Image();
      img.onload = () => resolve(true);
      img.onerror = () => resolve(false);
      img.src = url;
    });
  };

  // PrÃ©chargement des images
  React.useEffect(() => {
    filteredProducts.forEach(product => {
      const img = new Image();
      img.src = product.image;
    });
  }, [filteredProducts]);

  const getCategoryColor = (category) => {
    switch(category) {
      case 'Soins & BeautÃ©': return 'bg-purple-100 text-purple-800';
      case 'Boissons & Bien-Ãªtre': return 'bg-blue-100 text-blue-800';
      case 'ComplÃ©ments Alimentaires': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4">
        {/* En-tÃªte du catalogue */}
        <div className="mb-8">
