import React, { useState } from 'react';
import { products, categories } from '../../data/products';
import { useCart } from '../../context/CartContext';
import { Search, Filter, Star, Award, Tag, ChevronDown, ChevronUp } from 'lucide-react';

const CataloguePage = () => {
  const [selectedCategory, setSelectedCategory] = useState('Tous les produits');
  const [searchTerm, setSearchTerm] = useState('');
  const [expandedProduct, setExpandedProduct] = useState(null);
  const { addToCart } = useCart();

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'Tous les produits' || product.category === selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const toggleProductDetails = (productId) => {
    if (expandedProduct === productId) {
      setExpandedProduct(null);
    } else {
      setExpandedProduct(productId);
    }
  };

  const getCategoryColor = (category) => {
    switch(category) {
      case 'Soins & Beauté': return 'text-purple-700 bg-purple-100';
      case 'Boissons & Bien-être': return 'text-blue-700 bg-blue-100';
      case 'Compléments Alimentaires': return 'text-orange-700 bg-orange-100';
      default: return 'text-gray-700 bg-gray-100';
    }
  };

  const formatPrice = (price) => {
    return price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ' ');
  };

  return (
    <div className="min-h-screen py-8 bg-forever-gray-light">
      <div className="container mx-auto px-4">
        {/* Header */}
        <div className="mb-8 text-center">
          <h1 className="text-3xl md:text-4xl font-bold text-aloe-green-dark mb-4">
            Notre Catalogue Forever Living
          </h1>
          <p className="text-lg text-forever-gray-text max-w-2xl mx-auto">
            Découvrez nos produits 100% naturels à base d'Aloe Vera certifié
          </p>
        </div>

        {/* Filters */}
        <div className="mb-8 bg-white p-6 rounded-xl shadow-aloe">
          <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
            {/* Search */}
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-forever-gray-text" size={20} />
              <input
                type="text"
                placeholder="Rechercher un produit..."
                className="w-full pl-10 pr-4 py-3 border border-forever-gray-medium rounded-lg focus:outline-none focus:ring-2 focus:ring-aloe-green"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* Category Filter */}
            <div className="flex items-center space-x-2">
              <Filter size={20} className="text-forever-gray-text" />
              <select
                className="border border-forever-gray-medium rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-aloe-green"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Products Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredProducts.map(product => (
            <div key={product.id} className="product-card bg-white">
              <div className="relative">
                {/* Image Container */}
                <div className="h-64 bg-forever-gray-light flex items-center justify-center p-4">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="max-h-48 max-w-full object-contain"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = "/assets/images/produits/default-product.jpg";
                    }}
                  />
                </div>
                
                {/* Badges */}
                <div className="absolute top-3 left-3 flex flex-col gap-2">
                  <span className={`px-3 py-1 rounded-full text-xs font-bold ${getCategoryColor(product.category)}`}>
                    {product.category}
                  </span>
                  
                  {product.isBestSeller && (
                    <span className="flex items-center gap-1 px-3 py-1 rounded-full text-xs font-bold bg-aloe-gold text-gray-800">
                      <Star size={10} />
                      Best Seller
                    </span>
                  )}
                  
                  {product.isPromo && (
                    <span className="px-3 py-1 rounded-full text-xs font-bold bg-red-100 text-red-800">
                      <Tag size={10} className="inline mr-1" />
                      Promo
                    </span>
                  )}
                  
                  {product.isNew && (
                    <span className="px-3 py-1 rounded-full text-xs font-bold bg-green-100 text-green-800">
                      Nouveau
                    </span>
                  )}
                </div>
              </div>
              
              {/* Product Info */}
              <div className="p-6">
                <h3 className="font-bold text-xl text-aloe-green-dark mb-2">{product.name}</h3>
                
                <div className="mb-4">
                  <span className="text-2xl font-bold text-aloe-green">
                    {formatPrice(product.price)} {product.currency}
                  </span>
                  <p className="text-sm text-forever-gray-text mt-1">Réf: {product.code}</p>
                </div>
                
                {/* Short Description */}
                <p className="text-forever-gray-text mb-4 line-clamp-2">
                  {product.description}
                </p>
                
                {/* Expandable Details */}
                <div className="mb-4">
                  <button
                    onClick={() => toggleProductDetails(product.id)}
                    className="flex items-center text-aloe-green hover:text-aloe-green-dark font-medium"
                  >
                    {expandedProduct === product.id ? (
                      <>
                        <ChevronUp size={16} className="mr-1" />
                        Masquer les détails
                      </>
                    ) : (
                      <>
                        <ChevronDown size={16} className="mr-1" />
                        Voir les détails
                      </>
                    )}
                  </button>
                  
                  {expandedProduct === product.id && (
                    <div className="mt-3 p-4 bg-forever-gray-light rounded-lg animate-fade-in">
                      <h4 className="font-semibold text-aloe-green-dark mb-2">Avantages principaux:</h4>
                      <ul className="space-y-1 mb-3">
                        {product.benefits?.map((benefit, index) => (
                          <li key={index} className="flex items-start">
                            <span className="text-aloe-green mr-2">✓</span>
                            <span className="text-forever-gray-text">{benefit}</span>
                          </li>
                        ))}
                      </ul>
                      
                      <h4 className="font-semibold text-aloe-green-dark mb-2">Mode d'emploi:</h4>
                      <p className="text-forever-gray-text">{product.usage}</p>
                      
                      <div className="flex items-center mt-3">
                        <div className={`px-2 py-1 rounded text-xs font-bold ${
                          product.stock > 10 ? 'bg-green-100 text-green-800' :
                          product.stock > 5 ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }`}>
                          {product.stock > 10 ? 'En stock' : product.stock > 5 ? 'Stock limité' : 'Bientôt épuisé'}
                        </div>
                        <span className="text-sm text-forever-gray-text ml-2">
                          {product.stock} unité(s) disponible(s)
                        </span>
                      </div>
                    </div>
                  )}
                </div>
                
                {/* Add to Cart Button */}
                <button
                  onClick={() => addToCart(product)}
                  disabled={product.stock === 0}
                  className={`w-full py-3 rounded-lg font-semibold transition-all duration-300 ${
                    product.stock === 0
                      ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                      : 'btn-primary hover:bg-aloe-green-dark'
                  }`}
                >
                  {product.stock === 0 ? 'Rupture de stock' : 'Ajouter au panier'}
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Empty State */}
        {filteredProducts.length === 0 && (
          <div className="text-center py-12">
            <div className="text-forever-gray-medium mb-4">
              <Search size={64} className="mx-auto" />
            </div>
            <h3 className="text-xl font-semibold text-forever-gray-dark mb-2">
              Aucun produit trouvé
            </h3>
            <p className="text-forever-gray-text mb-4">
              Essayez de modifier vos critères de recherche ou sélectionnez une autre catégorie
            </p>
            <button
              onClick={() => {
                setSelectedCategory('Tous les produits');
                setSearchTerm('');
              }}
              className="btn-secondary"
            >
              Réinitialiser les filtres
            </button>
          </div>
        )}

        {/* Category Summary */}
        <div className="mt-12 pt-8 border-t border-forever-gray-medium">
          <h2 className="text-xl font-bold text-aloe-green-dark mb-6 text-center">
            Explorez nos catégories
          </h2>
          <div className="flex flex-wrap justify-center gap-3">
            {categories.slice(1).map(category => {
              const count = products.filter(p => p.category === category).length;
              return (
                <button
                  key={category}
                  onClick={() => setSelectedCategory(category)}
                  className={`px-4 py-2 rounded-full font-medium transition-colors ${
                    selectedCategory === category
                      ? 'bg-aloe-green text-white'
                      : 'bg-forever-gray-light text-forever-gray-dark hover:bg-forever-gray-medium'
                  }`}
                >
                  {category} ({count})
                </button>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CataloguePage;
