import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ChevronLeft, ChevronRight } from 'lucide-react';

const HeroSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      id: 1,
      image: "/assets/images/hero9.jpg",
      title: "Découvrez la Puissance de l'Aloe Vera",
      subtitle: "Des produits naturels pour votre santé et bien-être",
      price: "à partir de 15 000 FCFA",
      buttonText: "Acheter Maintenant",
      link: "/catalogue"
    },
    {
      id: 2,
      image: "/assets/images/hero9.jpg", 
      title: "Promotion Spéciale",
      subtitle: "Jusqu'à 25% de réduction sur les compléments",
      buttonText: "Profiter des Promotions",
      link: "/catalogue?promo=true"
    },
    {
      id: 3,
      image: "/assets/images/hero9.jpg", 
      title: "Nouvelle Collection Forever",
      subtitle: "Des compléments alimentaires innovants",
      buttonText: "Découvrir",
      link: "/catalogue"
    }
  ];

  // Auto-play du slider
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  return (
    <div className="wrap-main-slide mb-8 relative">
      <div className="relative h-[400px] md:h-[500px] lg:h-[600px] overflow-hidden rounded-xl shadow-xl">
        {/* Image de fond unique */}
        <div className="absolute inset-0">
          <img
            src={slides[currentSlide].image}
            alt={slides[currentSlide].title}
            className="w-full h-full object-cover transition-all duration-1000 ease-in-out"
            onError={(e) => {
              e.target.onerror = null;
              // Fallback images en ligne
              const fallbackImages = [
                "https://heros9./photo-1546069901-ba9599a7e63c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1600&q=80",
                "https://images.unsplash.com/photo-1584820926518-3ec36406b8fc?ixlib=rb-4.0.3&auto=format&fit=crop&w=1600&q=80",
                "https://images.unsplash.com/photo-1578500494198-246b612c5b9c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1600&q=80"
              ];
              e.target.src = fallbackImages[currentSlide];
              console.warn(`Image hero9.jpg non trouvée, utilisation du fallback`);
            }}
          />
          {/* Overlay avec couleur différente pour chaque slide */}
          <div 
            className="absolute inset-0 transition-all duration-500 ease-in-out"
            style={{
              background: currentSlide === 0 
                ? 'linear-gradient(90deg, rgba(27, 94, 32, 0.7) 0%, rgba(27, 94, 32, 0.4) 50%, transparent 100%)'
                : currentSlide === 1
                ? 'linear-gradient(90deg, rgba(156, 39, 176, 0.7) 0%, rgba(156, 39, 176, 0.4) 50%, transparent 100%)'
                : 'linear-gradient(90deg, rgba(33, 150, 243, 0.7) 0%, rgba(33, 150, 243, 0.4) 50%, transparent 100%)'
            }}
          />
        </div>

        {/* Contenu du slide actuel */}
        <div className="absolute inset-0 flex items-center transition-opacity duration-500">
          <div className="container mx-auto px-4">
            <div className={`max-w-2xl ${currentSlide === 1 ? 'text-center mx-auto' : ''}`}>
              <h2 className="text-3xl md:text-5xl lg:text-6xl font-bold text-white mb-4 leading-tight animate-fade-in">
                {slides[currentSlide].title}
              </h2>
              
              <span className="block text-lg md:text-xl lg:text-2xl text-white/90 mb-6 animate-fade-in animation-delay-200">
                {slides[currentSlide].subtitle}
              </span>
              
              {currentSlide === 1 && (
                <div className="mb-6 animate-fade-in animation-delay-400">
                  <p className="text-xl lg:text-2xl text-aloe-gold font-bold bg-white/20 inline-block px-4 py-2 rounded-lg">
                    CODE : VITALITE2024
                  </p>
                </div>
              )}
              
              {currentSlide === 1 && (
                <div className="mb-6 animate-fade-in animation-delay-400">
                  <h4 className="text-2xl lg:text-3xl text-white font-bold mb-1">
                    Livraison Gratuite
                  </h4>
                  <p className="text-lg text-white/80">
                    Pour toute commande de plus de 50 000 FCFA
                  </p>
                </div>
              )}
              
              {(currentSlide === 0 || currentSlide === 2) && (
                <p className="text-xl lg:text-2xl text-white mb-8 animate-fade-in animation-delay-600">
                  Prix: <span className="text-2xl lg:text-4xl font-bold text-aloe-gold ml-2">
                    {currentSlide === 0 ? "à partir de 15 000 FCFA" : "Dès 13 500 FCFA"}
                  </span>
                </p>
              )}
              
              <Link
                to={slides[currentSlide].link}
                className={`inline-block px-6 py-3 md:px-8 md:py-4 rounded-lg font-bold text-lg text-white transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-1 animate-fade-in animation-delay-800 ${
                  currentSlide === 0 
                    ? 'bg-aloe-green hover:bg-aloe-green-dark' 
                    : currentSlide === 1
                    ? 'bg-purple-600 hover:bg-purple-700'
                    : 'bg-blue-600 hover:bg-blue-700'
                }`}
              >
                {slides[currentSlide].buttonText}
              </Link>
            </div>
          </div>
        </div>

        {/* Boutons de navigation */}
        <button
          onClick={prevSlide}
          className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-white/20 hover:bg-white/30 text-white p-2 rounded-full transition-all duration-300 backdrop-blur-sm"
          aria-label="Slide précédent"
        >
          <ChevronLeft size={24} />
        </button>
        
        <button
          onClick={nextSlide}
          className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-white/20 hover:bg-white/30 text-white p-2 rounded-full transition-all duration-300 backdrop-blur-sm"
          aria-label="Slide suivant"
        >
          <ChevronRight size={24} />
        </button>

        {/* Indicateurs de slide */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentSlide(index)}
              className={`w-3 h-3 rounded-full transition-all duration-300 ${
                index === currentSlide 
                  ? 'bg-white w-8' 
                  : 'bg-white/50 hover:bg-white/70'
              }`}
              aria-label={`Aller au slide ${index + 1}`}
            />
          ))}
        </div>
      </div>

      {/* Styles CSS pour les animations */}
      <style jsx>{`
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .animate-fade-in {
          animation: fadeIn 0.8s ease-out forwards;
        }
        
        .animation-delay-200 {
          animation-delay: 200ms;
        }
        
        .animation-delay-400 {
          animation-delay: 400ms;
        }
        
        .animation-delay-600 {
          animation-delay: 600ms;
        }
        
        .animation-delay-800 {
          animation-delay: 800ms;
        }
      `}</style>
    </div>
  );
};

export default HeroSlider;
