import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import { Link } from 'react-router-dom';

const HeroSlider = () => {
  const slides = [
    {
      id: 1,
      image: "/assets/images/hero.jpg", // Assurez-vous que ce fichier existe
      title: "Découvrez la Puissance de l'Aloe Vera",
      subtitle: "Des produits naturels pour votre santé et bien-être",
      price: "à partir de 15 000 FCFA",
      buttonText: "Acheter Maintenant",
      link: "/catalogue"
    },
    {
      id: 2,
      image: "/assets/images/banner-1.jpg", // Image différente pour la 2ème slide
      title: "Promotion Spéciale",
      subtitle: "Jusqu'à 25% de réduction",
      discount: "CODE : VITALITE2024",
      highlight: "Livraison Gratuite",
      buttonText: "Profiter des Promotions",
      link: "/catalogue?promo=true"
    },
    {
      id: 3,
      image: "/assets/images/banner-2.jpg", // Image différente pour la 3ème slide
      title: "Nouvelle Collection Forever",
      subtitle: "Des compléments alimentaires innovants",
      price: "Dès 13 500 FCFA",
      buttonText: "Découvrir",
      link: "/catalogue?category=Compléments+Alimentaires"
    }
  ];

  return (
    <div className="wrap-main-slide mb-8">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={0}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        autoplay={{ delay: 5000 }}
        loop={true}
        className="hero-slider"
      >
        {slides.map((slide) => (
          <SwiperSlide key={slide.id}>
            <div className="relative">
              {/* Image de fond avec fallback */}
              <div className="h-[400px] md:h-[500px] lg:h-[600px] relative overflow-hidden">
                <img
                  src={slide.image}
                  alt={slide.title}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    e.target.onerror = null;
                    // Fallback images en ligne si les locales ne chargent pas
                    const fallbackImages = [
                      "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1600&q=80",
                      "https://images.unsplash.com/photo-1584820926518-3ec36406b8fc?ixlib=rb-4.0.3&auto=format&fit=crop&w=1600&q=80",
                      "https://images.unsplash.com/photo-1578500494198-246b612c5b9c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1600&q=80"
                    ];
                    e.target.src = fallbackImages[slide.id - 1];
                    console.warn(`Image ${slide.image} non trouvée, utilisation du fallback`);
                  }}
                />
                {/* Overlay pour améliorer la lisibilité du texte */}
                <div className="absolute inset-0 bg-gradient-to-r from-green-900/70 via-green-800/50 to-transparent"></div>
              </div>

              {/* Contenu du slide */}
              <div className="absolute inset-0 flex items-center">
                <div className="container mx-auto px-4">
                  <div className={`slide-info max-w-2xl ${slide.id === 2 ? 'text-center' : ''}`}>
                    <h2 className="f-title text-3xl md:text-5xl lg:text-6xl font-bold text-white mb-4 leading-tight">
                      {slide.title}
                    </h2>
                    
                    {slide.subtitle && (
                      <span className="subtitle block text-lg md:text-xl lg:text-2xl text-white/90 mb-4">
                        {slide.subtitle}
                      </span>
                    )}
                    
                    {slide.discount && (
                      <div className="mb-4">
                        <p className="discount-code text-xl lg:text-2xl text-aloe-gold font-bold bg-white/20 inline-block px-4 py-2 rounded-lg">
                          {slide.discount}
                        </p>
                      </div>
                    )}
                    
                    {slide.highlight && (
                      <div className="mb-4">
                        <h4 className="s-title text-2xl lg:text-3xl text-white font-bold mb-1">
                          {slide.highlight}
                        </h4>
                        <p className="s-subtitle text-lg text-white/80">
                          Pour toute commande de plus de 50 000 FCFA
                        </p>
                      </div>
                    )}
                    
                    {slide.price && (
                      <p className="sale-info text-xl lg:text-2xl text-white mb-6">
                        Prix: <span className="price text-2xl lg:text-4xl font-bold text-aloe-gold ml-2">
                          {slide.price}
                        </span>
                      </p>
                    )}
                    
                    <Link
                      to={slide.link}
                      className="btn-link inline-block bg-aloe-green text-white px-6 py-3 md:px-8 md:py-4 rounded-lg font-bold text-lg hover:bg-aloe-green-dark transition-colors shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-transform duration-300"
                    >
                      {slide.buttonText}
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>

      {/* Styles inline pour Swiper */}
      <style jsx>{`
        .hero-slider {
          border-radius: 12px;
          overflow: hidden;
          box-shadow: 0 10px 30px rgba(0,0,0,0.1);
        }
        
        .hero-slider .swiper-button-next,
        .hero-slider .swiper-button-prev {
          color: white;
          background: rgba(46, 125, 50, 0.8);
          width: 50px;
          height: 50px;
          border-radius: 50%;
          transition: all 0.3s ease;
        }
        
        .hero-slider .swiper-button-next:hover,
        .hero-slider .swiper-button-prev:hover {
          background: rgba(46, 125, 50, 1);
          transform: scale(1.1);
        }
        
        .hero-slider .swiper-button-next:after,
        .hero-slider .swiper-button-prev:after {
          font-size: 20px;
          font-weight: bold;
        }
        
        .hero-slider .swiper-pagination-bullet {
          background: white;
          opacity: 0.5;
          width: 12px;
          height: 12px;
        }
        
        .hero-slider .swiper-pagination-bullet-active {
          background: #2E7D32;
          opacity: 1;
          transform: scale(1.2);
        }
        
        @media (max-width: 768px) {
          .hero-slider .swiper-button-next,
          .hero-slider .swiper-button-prev {
            width: 40px;
            height: 40px;
          }
          
          .hero-slider .swiper-button-next:after,
          .hero-slider .swiper-button-prev:after {
            font-size: 16px;
          }
        }
      `}</style>
    </div>
  );
};

export default HeroSlider;
