import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCart, Phone, Menu, X } from 'lucide-react';
import { useCart } from '../context/CartContext';

const WHATSAPP_NUMBER = '221771234567';
const WHATSAPP_MESSAGE =
  'Bonjour, je suis intéressé(e) par vos produits Vitalité & Bien-être au Naturel.';

const Header = () => {
  const { getItemCount } = useCart();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const menuItems = [
    { name: 'Accueil', path: '/' },
    { name: 'Catalogue', path: '/catalogue' },
    { name: 'À Propos', path: '/apropos' },
    { name: 'Contact', path: '/contact' },
  ];

  const whatsappLink = `https://wa.me/${WHATSAPP_NUMBER}?text=${encodeURIComponent(
    WHATSAPP_MESSAGE
  )}`;

  return (
    <header className="bg-white/90 backdrop-blur-md shadow-lg sticky top-0 z-50">
      <div className="container mx-auto px-4 py-3">
        <div className="flex justify-between items-center">

          {/* LOGO */}
          <Link to="/" className="flex items-center space-x-3 group">
            <img
  src="/images/logo1.png"
  alt="Vitalité & Bien-être au Naturel"
  className="h-14 sm:h-16 md:h-20 w-auto
             animate-[fadeIn_0.8s_ease-out]"
/>

          
          </Link>

          {/* NAVIGATION DESKTOP */}
          <nav className="hidden md:flex items-center space-x-8">
            {menuItems.map((item) => (
              <Link
                key={item.name}
                to={item.path}
                className="relative text-forever-gray-dark font-medium transition-colors hover:text-aloe-green after:absolute after:left-0 after:-bottom-1 after:h-0.5 after:w-0 after:bg-aloe-green after:transition-all hover:after:w-full"
              >
                {item.name}
              </Link>
            ))}
          </nav>

          {/* ACTIONS */}
          <div className="flex items-center space-x-4">

            {/* WHATSAPP */}
            <a
              href={whatsappLink}
              target="_blank"
              rel="noopener noreferrer"
              className="hidden md:flex items-center space-x-2 px-5 py-2 rounded-full bg-green-500 text-white font-semibold shadow-md hover:bg-green-600 hover:shadow-lg transition-all duration-300"
            >
              <Phone size={18} />
              <span>WhatsApp</span>
            </a>

            {/* PANIER */}
            <Link
              to="/chariots"
              className="relative p-2 rounded-full hover:bg-gray-100 transition"
            >
              <ShoppingCart size={24} />
              {getItemCount() > 0 && (
                <span className="absolute -top-1 -right-1 bg-aloe-gold text-black text-xs rounded-full w-5 h-5 flex items-center justify-center font-bold animate-pulse">
                  {getItemCount()}
                </span>
              )}
            </Link>

            {/* MENU MOBILE */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden p-2 rounded-lg hover:bg-gray-100 transition"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* MENU MOBILE */}
        <div
          className={`md:hidden overflow-hidden transition-all duration-300 ${
            isMenuOpen ? 'max-h-96 mt-4' : 'max-h-0'
          }`}
        >
          <div className="flex flex-col space-y-3 pt-4 border-t">
            {menuItems.map((item) => (
              <Link
                key={item.name}
                to={item.path}
                className="py-2 text-lg font-medium text-forever-gray-dark hover:text-aloe-green transition"
                onClick={() => setIsMenuOpen(false)}
              >
                {item.name}
              </Link>
            ))}

            <a
              href={whatsappLink}
              target="_blank"
              rel="noopener noreferrer"
              className="flex items-center justify-center space-x-2 py-3 mt-3 rounded-full bg-green-500 text-white font-semibold shadow-md hover:bg-green-600 transition"
              onClick={() => setIsMenuOpen(false)}
            >
              <Phone size={18} />
              <span>Commander sur WhatsApp</span>
            </a>
          </div>
        </div>

      </div>
    </header>
  );
};

export default Header;
