import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';
import { Link } from 'react-router-dom';
import { CreditCard, Truck, Shield, CheckCircle, Package, Home, ShoppingBag } from 'lucide-react';
import emailjs from '@emailjs/browser';

const CaissePage = () => {
  const { cart, getCartTotal, clearCart } = useCart();
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    postalCode: '',
    paymentMethod: 'mobile_money',
    cardNumber: '',
    cardExpiry: '',
    cardCVC: '',
    cardName: ''
  });
  const [isProcessing, setIsProcessing] = useState(false);
  const [orderComplete, setOrderComplete] = useState(false);
  const [orderNumber, setOrderNumber] = useState('');

  /* ================= EMAILJS (INTÉGRATION PROPRE) ================= */

  const SERVICE_ID = 'service_pz3hy7y';
  const TEMPLATE_ID = 'template_i42e22d';
  const PUBLIC_KEY = '4DQeOzWkFTSeIQB-M';

  const sendOrderEmail = (orderNum) => {
    const templateParams = {
      from_name: formData.fullName,
      from_email: formData.email,
      phone: formData.phone,
      address: formData.address,
      city: formData.city,
      payment_method: formData.paymentMethod,
      order_number: orderNum,
      total: `${getCartTotal().toLocaleString()} FCFA`,
      items: cart.map(item => `${item.name} x${item.quantity}`).join(', ')
    };

    emailjs
      .send(SERVICE_ID, TEMPLATE_ID, templateParams, PUBLIC_KEY)
      .then(() => {
        console.log('✅ Email commande envoyé');
      })
      .catch((error) => {
        console.error('❌ Erreur EmailJS :', error);
      });
  };

  /* ================= FORM ================= */

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (step === 3) {
      setIsProcessing(true);

      setTimeout(() => {
        const newOrderNumber = `CMD-${Date.now().toString().slice(-8)}`;

        setOrderNumber(newOrderNumber);
        setOrderComplete(true);
        clearCart();

        // ✅ ENVOI EMAIL ICI
        sendOrderEmail(newOrderNumber);

        setIsProcessing(false);
      }, 2000);
    } else {
      setStep(step + 1);
    }
  };

  /* ================= PANIER VIDE ================= */

  if (cart.length === 0 && !orderComplete) {
    return (
      <div className="min-h-screen py-16 text-center">
        <Package size={80} className="mx-auto text-gray-300 mb-4" />
        <h2 className="text-2xl font-bold">Votre panier est vide</h2>
        <Link to="/catalogue" className="text-green-600 font-semibold">
          Retour au catalogue
        </Link>
      </div>
    );
  }

  /* ================= COMMANDE OK ================= */

  if (orderComplete) {
    return (
      <div className="min-h-screen py-16 text-center">
        <CheckCircle size={80} className="mx-auto text-green-600 mb-4" />
        <h1 className="text-3xl font-bold">Commande confirmée</h1>
        <p className="mt-2">Numéro : <strong>{orderNumber}</strong></p>

        <div className="mt-6 flex justify-center gap-4">
          <Link to="/" className="text-green-600 flex items-center">
            <Home className="mr-2" /> Accueil
          </Link>
        </div>
      </div>
    );
  }

  /* ================= FORMULAIRE ================= */

  return (
    <form onSubmit={handleSubmit} className="max-w-3xl mx-auto p-6 bg-white rounded-xl shadow">
      <h1 className="text-2xl font-bold mb-6">Finaliser la commande</h1>

      <input
        name="fullName"
        placeholder="Nom complet"
        value={formData.fullName}
        onChange={handleInputChange}
        required
        className="w-full mb-3 border p-2 rounded"
      />

      <input
        name="email"
        type="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleInputChange}
        required
        className="w-full mb-3 border p-2 rounded"
      />

      <textarea
        name="address"
        placeholder="Adresse"
        value={formData.address}
        onChange={handleInputChange}
        required
        className="w-full mb-3 border p-2 rounded"
      />

      <button
        type="submit"
        disabled={isProcessing}
        className="bg-green-600 text-white px-6 py-3 rounded"
      >
        {isProcessing ? 'Traitement...' : 'Confirmer la commande'}
      </button>
    </form>
  );
};

export default CaissePage;
