import React, { useState } from 'react';
import { Mail, Phone, MapPin, Send, MessageCircle, Clock, Users } from 'lucide-react';

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: 'Demande générale',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulation d'envoi (remplace par emailjs.sendForm)
    setTimeout(() => {
      setIsSubmitting(false);
      setSubmitSuccess(true);
      setFormData({ name: '', email: '', phone: '', subject: 'Demande générale', message: '' });
      setTimeout(() => setSubmitSuccess(false), 5000);
    }, 1500);
  };

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="relative bg-gradient-to-br from-green-50 to-emerald-100 py-16 md:py-24 overflow-hidden">
        <div className="absolute inset-0 z-0">
          <div className="absolute inset-0 bg-gradient-to-r from-green-600/10 to-emerald-600/10"></div>
          <img
            src="/assets/images/hero5.png"
            alt="Contact - Vitalité & Bien-être au Naturel"
            className="w-full h-full object-cover opacity-20"
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = "data:image/svg+xml,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 1200 400'%3E%3Crect width='1200' height='400' fill='%232E7D32'/%3E%3Ctext x='600' y='200' font-family='Arial' font-size='48' fill='white' text-anchor='middle' dy='.3em'%3EContactez-nous%3C/text%3E%3C/svg%3E";
            }}
          />
        </div>

        <div className="container relative z-10 mx-auto px-4 text-center">
          <div className="max-w-3xl mx-auto">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-aloe-green rounded-full mb-6 animate-bounce">
              <MessageCircle className="text-white" size={28} />
            </div>
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-aloe-green-dark mb-4 animate-slide-up">
              Contactez-nous
            </h1>
            <p className="text-xl text-gray-700 mb-8 animate-slide-up animation-delay-200">
              Nous sommes là pour répondre à vos questions et vous accompagner dans votre bien-être
            </p>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-8 animate-fade-in animation-delay-400">
              {[
                { icon: Phone, label: 'Appelez-nous', value: '+221 76 949 06 85' },
                { icon: Mail, label: 'Écrivez-nous', value: 'ssene07@gmail.com' },
                { icon: Clock, label: 'Horaires', value: 'Lun-Ven: 8h-20h' }
              ].map((item, i) => (
                <div key={i} className="bg-white/80 backdrop-blur-sm rounded-xl p-4 hover:-translate-y-1 transition-transform duration-300">
                  <item.icon className="text-aloe-green mx-auto mb-2" size={24} />
                  <p className="font-semibold">{item.label}</p>
                  <p className="text-sm text-gray-600">{item.value}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Main Section */}
      <div className="container mx-auto px-4 py-12 grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Contact Form */}
        <div className="animate-slide-up animation-delay-300">
          <div className="bg-white rounded-2xl shadow-xl p-6 md:p-8 hover:shadow-2xl transition-all duration-500">
            <div className="flex items-center mb-6">
              <div className="w-10 h-10 bg-aloe-green rounded-full flex items-center justify-center mr-3">
                <Send className="text-white" size={20} />
              </div>
              <h2 className="text-2xl font-bold text-aloe-green-dark">Envoyez-nous un message</h2>
            </div>

            {submitSuccess && (
              <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg animate-fade-in">
                <p className="font-semibold text-green-800">Message envoyé avec succès !</p>
                <p className="text-green-600 text-sm">Nous vous répondrons dans les plus brefs délais.</p>
              </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {[
                  { label: 'Nom complet', name: 'name', type: 'text', placeholder: 'Votre nom', required: true },
                  { label: 'Email', name: 'email', type: 'email', placeholder: 'votre@email.com', required: true },
                  { label: 'Téléphone', name: 'phone', type: 'tel', placeholder: '+221 XX XXX XX XX', required: false }
                ].map((field, i) => (
                  <div key={i} className="space-y-2">
                    <label className="block text-sm font-medium text-gray-700">{field.label}</label>
                    <input
                      type={field.type}
                      name={field.name}
                      required={field.required}
                      placeholder={field.placeholder}
                      value={formData[field.name]}
                      onChange={handleChange}
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-aloe-green focus:border-transparent transition-all duration-300"
                    />
                  </div>
                ))}
              </div>

              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-700">Sujet</label>
                <select
                  name="subject"
                  value={formData.subject}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-aloe-green focus:border-transparent transition-all duration-300"
                >
                  {['Demande générale', 'Commande', 'Support produit', 'Partenariat', 'Autre'].map((opt) => (
                    <option key={opt} value={opt}>{opt}</option>
                  ))}
                </select>
              </div>

              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-700">Message</label>
                <textarea
                  name="message"
                  rows="6"
                  placeholder="Comment pouvons-nous vous aider ?"
                  value={formData.message}
                  onChange={handleChange}
                  required
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-aloe-green focus:border-transparent transition-all duration-300"
                />
              </div>

              <button
                type="submit"
                disabled={isSubmitting}
                className={`w-full py-4 rounded-lg font-semibold flex items-center justify-center gap-3 transition-all duration-300 transform hover:scale-[1.02] ${
                  isSubmitting ? 'bg-gray-400 cursor-not-allowed' : 'bg-aloe-green text-white hover:bg-aloe-green-dark'
                }`}
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                    <span>Envoi en cours...</span>
                  </>
                ) : (
                  <>
                    <Send size={20} />
                    <span>Envoyer le message</span>
                  </>
                )}
              </button>
            </form>
          </div>
        </div>

        {/* Contact Info */}
        <div className="space-y-6">
          <div className="bg-white rounded-2xl shadow-xl p-6 md:p-8 animate-slide-up animation-delay-400">
            <h2 className="text-2xl font-bold text-aloe-green-dark mb-4 flex items-center gap-3">
              <Users className="text-aloe-green" size={24} /> Nos coordonnées
            </h2>

            <div className="space-y-4">
              <div className="flex items-start gap-4 p-4 bg-green-50 rounded-xl hover:bg-green-100 transition-all duration-300">
                <div className="bg-aloe-green p-3 rounded-lg">
                  <Phone className="text-white" size={20} />
                </div>
                <div>
                  <h3 className="font-semibold">Téléphone</h3>
                  <a href="tel:+221769490685" className="block text-gray-700 hover:text-aloe-green transition-colors">+221 76 949 06 85</a>
                  <p className="text-sm text-gray-500">Lun-Ven: 8h-20h, Sam: 9h-13h</p>
                  <a href="https://wa.me/221769490685" target="_blank" rel="noopener noreferrer"
                    className="inline-flex items-center gap-2 mt-2 bg-whatsapp-green text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors">
                    <MessageCircle size={16} /> WhatsApp
                  </a>
                </div>
              </div>

              <div className="flex items-start gap-4 p-4 bg-green-50 rounded-xl hover:bg-green-100 transition-all duration-300">
                <div className="bg-aloe-green p-3 rounded-lg">
                  <Mail className="text-white" size={20} />
                </div>
                <div>
                  <h3 className="font-semibold">Email</h3>
                  <a href="mailto:ssene07@gmail.com" className="block text-gray-700 hover:text-aloe-green transition-colors">ssene07@gmail.com</a>
                  <p className="text-sm text-gray-500">Réponse sous 24 heures</p>
                </div>
              </div>

              <div className="flex items-start gap-4 p-4 bg-green-50 rounded-xl hover:bg-green-100 transition-all duration-300">
                <div className="bg-aloe-green p-3 rounded-lg">
                  <MapPin className="text-white" size={20} />
                </div>
                <div>
                  <h3 className="font-semibold">Adresse</h3>
                  <p>Cité F9, Thiés</p>
                  <p>Sénégal</p>
                  <p className="text-sm text-gray-500">Sur rendez-vous uniquement</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContactPage;
