import React, { useState } from 'react';
import { Mail, Phone, MapPin, Send, MessageCircle, Clock } from 'lucide-react';
import emailjs from '@emailjs/browser';

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: 'Demande générale',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await emailjs.send(
        'service_pz3hy7y', // ✅ TON SERVICE ID
        'template_i42e22d', // ✅ TON TEMPLATE ID
        {
          from_name: formData.name,
          from_email: formData.email,
          phone: formData.phone,
          subject: formData.subject,
          message: formData.message
        },
        '4DQeOzWkFTSeIQB-M' // ✅ TA PUBLIC KEY
      );

      setSubmitSuccess(true);
      setFormData({
        name: '',
        email: '',
        phone: '',
        subject: 'Demande générale',
        message: ''
      });

      setTimeout(() => setSubmitSuccess(false), 5000);
    } catch (error) {
      console.error('EmailJS error:', error);
      alert("Impossible d'envoyer le message. Vérifie la configuration EmailJS.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen">
      {/* Hero */}
      <section className="relative bg-gradient-to-br from-green-50 to-emerald-100 py-16 md:py-24">
        <div className="container mx-auto px-4 text-center">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-aloe-green rounded-full mb-6 animate-bounce">
            <MessageCircle className="text-white" size={28} />
          </div>

          <h1 className="text-4xl md:text-5xl font-bold text-aloe-green-dark mb-4">
            Contactez-nous
          </h1>
          <p className="text-xl text-gray-700">
            Nous sommes là pour répondre à vos questions
          </p>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-8">
            <div className="bg-white rounded-xl p-4">
              <Phone className="mx-auto mb-2 text-aloe-green" />
              <p className="font-semibold">Téléphone</p>
              <p className="text-sm">+221 76 949 06 85</p>
            </div>
            <div className="bg-white rounded-xl p-4">
              <Mail className="mx-auto mb-2 text-aloe-green" />
              <p className="font-semibold">Email</p>
              <p className="text-sm">ssene07@gmail.com</p>
            </div>
            <div className="bg-white rounded-xl p-4">
              <Clock className="mx-auto mb-2 text-aloe-green" />
              <p className="font-semibold">Horaires</p>
              <p className="text-sm">Lun–Ven : 8h–20h</p>
            </div>
          </div>
        </div>
      </section>

      {/* Formulaire */}
      <div className="container mx-auto px-4 py-12">
        <div className="max-w-3xl mx-auto bg-white p-8 rounded-2xl shadow-xl">
          <h2 className="text-2xl font-bold mb-6 text-aloe-green-dark">
            Envoyez-nous un message
          </h2>

          {submitSuccess && (
            <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg">
              <p className="font-semibold text-green-700">
                Message envoyé avec succès !
              </p>
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            <input
              type="text"
              name="name"
              placeholder="Votre nom"
              required
              value={formData.name}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg"
            />

            <input
              type="email"
              name="email"
              placeholder="Votre email"
              required
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg"
            />

            <input
              type="tel"
              name="phone"
              placeholder="Téléphone"
              value={formData.phone}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg"
            />

            <select
              name="subject"
              value={formData.subject}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg"
            >
              <option>Demande générale</option>
              <option>Commande</option>
              <option>Support produit</option>
              <option>Partenariat</option>
            </select>

            <textarea
              name="message"
              rows="5"
              placeholder="Votre message"
              required
              value={formData.message}
              onChange={handleChange}
              className="w-full px-4 py-3 border rounded-lg"
            />

            <button
              type="submit"
              disabled={isSubmitting}
              className={`w-full py-3 rounded-lg text-white font-semibold ${
                isSubmitting ? 'bg-gray-400' : 'bg-aloe-green hover:bg-aloe-green-dark'
              }`}
            >
              {isSubmitting ? 'Envoi en cours...' : 'Envoyer le message'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ContactPage;
