import React from 'react';
import HeroSlider from '../../components/HeroSlider';
import Banners from '../../components/Banners';
import ProductsCarousel from '../../components/ProductsCarousel';
import LatestProducts from '../../components/LatestProducts';
import { Leaf, Truck, Shield, Star } from 'lucide-react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <div className="min-h-screen">
      {/* Slider Principal */}
      <div className="container mx-auto px-4 py-6">
        <HeroSlider />
      </div>

      {/* Bannières */}
      <div className="container mx-auto px-4 mb-12">
        <Banners />
      </div>

      {/* Produits en Promotion */}
      <div className="container mx-auto px-4 mb-16">
        <ProductsCarousel 
          title="Produits en Promotion"
          showCountdown={true}
          filter="sale"
          limit={8}
        />
      </div>

      {/* Pourquoi Nous Choisir */}
      <section className="py-16 bg-gray-50">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-aloe-green-dark mb-4">
              Pourquoi Choisir Forever Living ?
            </h2>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto">
              La qualité et l'authenticité font la différence
            </p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {[
              {
                icon: Leaf,
                title: "100% Naturel",
                description: "Produits à base d'Aloe Vera pur, sans produits chimiques agressifs"
              },
              {
                icon: Shield,
                title: "Qualité Certifiée",
                description: "Contrôle qualité strict Forever Living, normes internationales"
              },
              {
                icon: Truck,
                title: "Livraison Rapide",
                description: "Expédition sous 24-48h partout au Sénégal"
              },
              {
                icon: Star,
                title: "Satisfaction Garantie",
                description: "Retour gratuit sous 14 jours si vous n'êtes pas satisfait"
              }
            ].map((item, index) => (
              <div key={index} className="text-center bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-shadow">
                <div className="w-16 h-16 bg-aloe-green rounded-full flex items-center justify-center mx-auto mb-4">
                  <item.icon className="text-white" size={32} />
                </div>
                <h3 className="font-bold text-xl mb-3 text-aloe-green-dark">{item.title}</h3>
                <p className="text-gray-600">{item.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Nouveaux Produits */}
      <div className="container mx-auto px-4 py-16">
        <LatestProducts />
      </div>

      {/* Call to Action */}
      <section className="py-16 bg-gradient-to-r from-aloe-green to-aloe-green-dark text-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl font-bold mb-6">
            Prêt à Découvrir les Bienfaits de l'Aloe Vera ?
          </h2>
          <p className="text-xl mb-8 max-w-2xl mx-auto">
            Rejoignez des milliers de clients satisfaits au Sénégal
          </p>
          <div className="flex flex-col sm:flex-row justify-center gap-4">
            <Link
              to="/catalogue"
              className="bg-white text-aloe-green px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors"
            >
              Voir le catalogue complet
            </Link>
            <a 
              href="https://wa.me/221769490685"
              target="_blank"
              rel="noopener noreferrer"
              className="bg-aloe-gold text-gray-800 px-8 py-3 rounded-lg font-semibold hover:bg-yellow-500 transition-colors"
            >
              Nous contacter sur WhatsApp
            </a>
          </div>
       
       
       
       
        </div>
      </section>
    </div>
  );
};

export default HomePage;
