import React from 'react';
import { Link } from 'react-router-dom';
import { products } from '../../data/products';
import { Leaf, Truck, Shield, Star, CheckCircle } from 'lucide-react';
import { motion } from 'framer-motion';
import PaymentSuccess from "../PaymentSuccess";



const handleConfirmPayment = () => {
  if (!paymentMethod) {
    alert("Veuillez choisir un mode de paiement");
    return;
  }

  // sauvegarde éventuelle
  // localStorage.setItem("lastOrder", JSON.stringify(order));

  clearCart();
  navigate("/paiement-confirme");
};

const HomePage = () => {
  const featuredProducts = products
    .filter(p => p.tags && p.tags.includes("best-seller"))
    .slice(0, 4);

  return (
    <div className="min-h-screen">

      {/* Hero Section */}
      <section className="bg-gradient-to-r from-green-50 to-emerald-50 py-12 md:py-20 overflow-hidden">
        <div className="container mx-auto px-4">
          <div className="flex flex-col md:flex-row items-center">

            {/* Texte */}
            <motion.div
              className="md:w-1/2 mb-8 md:mb-0"
              initial={{ opacity: 0, x: -30 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6 }}
            >
              <div className="flex items-center mb-4">
                <div className="w-12 h-12 mr-3">
                  
                </div>

                <h1 className="text-4xl md:text-5xl font-bold text-aloe-green-dark">
                  Vitalité & Bien-être au Naturel
                  <span className="block text-2xl md:text-3xl text-aloe-green">
                  </span>
                </h1>
              </div>

              <p className="text-lg text-gray-600 mb-6">
                Découvrez la puissance de l'Aloe Vera Forever Living.
                Des produits naturels et certifiés pour votre santé et votre bien-être.
              </p>

              <div className="flex flex-wrap gap-4 mb-6">
                {[
                  "Produits 100% naturels",
                  "Livraison au Sénégal",
                  "Paiement sécurisé"
                ].map((text, i) => (
                  <motion.div
                    key={i}
                    className="flex items-center"
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.3 + i * 0.15 }}
                  >
                    <CheckCircle className="text-aloe-green mr-2" size={20} />
                    <span className="text-sm">{text}</span>
                  </motion.div>
                ))}
              </div>

             <div className="space-y-4">
  <div className="flex flex-col sm:flex-row gap-4">
    <Link 
      to="/catalogue" 
      className="group relative overflow-hidden bg-gradient-to-r from-aloe-green to-aloe-green-dark text-white px-6 py-3 rounded-xl font-semibold text-center hover:shadow-xl transition-all duration-300 transform hover:scale-105 flex-1"
    >
      <span className="relative flex items-center justify-center gap-2">
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
        </svg>
        Voir tous les produits
      </span>
    </Link>
    
    {/* Bouton WhatsApp avec message de conseil */}
    <a
      href="https://wa.me/221777131362?text=Bonjour%20👋%20Je%20voudrais%20un%20conseil%20pour%20choisir%20le%20meilleur%20produit%20Aloe%20Vera%20adapté%20à%20mes%20besoins."
      target="_blank"
      rel="noopener noreferrer"
      className="group relative overflow-hidden bg-gradient-to-r from-blue-500 to-cyan-500 text-white px-6 py-3 rounded-xl font-semibold text-center hover:shadow-xl transition-all duration-300 transform hover:scale-105 flex-1"
    >
      <span className="relative flex items-center justify-center gap-2">
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
        </svg>
        Demander conseil
      </span>
    </a>
  </div>
  
  <div className="flex flex-col sm:flex-row gap-4">
    {/* Bouton WhatsApp avec message de commande */}
    <a
      href="https://wa.me/221777131362?text=Bonjour%20👋%20Je%20souhaite%20commander%20des%20produits%20Forever%20Aloe%20Vera.%20Pouvez-vous%20m'indiquer%20les%20étapes%20pour%20passer%20commande%20?"
      target="_blank"
      rel="noopener noreferrer"
      className="group relative overflow-hidden bg-gradient-to-r from-whatsapp-green to-green-500 text-white px-6 py-3 rounded-xl font-semibold text-center hover:shadow-xl transition-all duration-300 transform hover:scale-105 flex-1"
    >
      <span className="relative flex items-center justify-center gap-2">
        <img 
          src="https://cdn-icons-png.flaticon.com/512/220/220236.png" 
          className="w-5 h-5" 
          alt="WhatsApp" 
        />
        Commander maintenant
      </span>
    </a>
    
    {/* Bouton WhatsApp avec message spécifique produit */}
    <a
      href="https://wa.me/221777131362?text=Bonjour%20👋%20J'ai%20vu%20le%20produit%20[Indiquer%20le%20nom%20du%20produit]%20sur%20votre%20site%20et%20je%20suis%20intéressé(e).%20Pouvez-vous%20me%20donner%20plus%20d'informations%20et%20le%20prix%20?"
      target="_blank"
      rel="noopener noreferrer"
      className="group relative overflow-hidden bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-3 rounded-xl font-semibold text-center hover:shadow-xl transition-all duration-300 transform hover:scale-105 flex-1"
    >
      <span className="relative flex items-center justify-center gap-2">
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
        Info sur un produit
      </span>
    </a>
  </div>
</div>
            </motion.div>

            {/* Image */}
            <motion.div
              className="md:w-1/2 mt-8 md:mt-0"
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.6 }}
            >
              <div className="relative">
                <motion.div
                  className="bg-white p-4 rounded-xl shadow-aloe"
                  whileHover={{ scale: 1.03 }}
                >
                  <img
                    src="/assets/images/heros.jpg"
                    alt="Produits Forever Aloe Vera"
                    className="w-full h-auto rounded-lg object-cover"
                  />
                </motion.div>

                <motion.div
                  className="absolute -top-4 -left-4 bg-aloe-gold text-gray-800 px-4 py-2 rounded-lg font-bold shadow-lg"
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.6 }}
                >
              <div className="flex items-center gap-2">
  <Leaf size={18} />
  <span>Naturel</span>
</div>

                </motion.div>

                <motion.div
                  className="absolute -bottom-4 -right-4 bg-aloe-green text-white px-4 py-2 rounded-lg font-bold shadow-lg"
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.8 }}
                >
                  ✅ Certifié
                </motion.div>
              </div>
            </motion.div>

          </div>
        </div>
      </section>
 {/* Featured Products */}
      <section className="py-16 bg-white">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-aloe-green-dark mb-4">
              Nos Produits Phares
            </h2>
            <p className="text-lg text-forever-gray-text max-w-2xl mx-auto">
              Découvrez notre sélection de produits Forever Living les plus populaires
            </p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {featuredProducts.map((product) => (
              <div key={product.id} className="product-card group">
                <div className="relative h-48 bg-forever-gray-light flex items-center justify-center p-4 overflow-hidden">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="max-h-full max-w-full object-contain transition-transform duration-300 group-hover:scale-105"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = "/assets/images/default_product.jpg";
                    }}
                  />
                  
                  {product.tags && product.tags.includes("best-seller") && (
                    <div className="absolute top-3 left-3 bg-aloe-gold text-gray-800 px-3 py-1 rounded-full text-xs font-bold flex items-center">
                      <Star size={10} className="mr-1" />
                      Best Seller
                    </div>
                  )}
                </div>
                
                <div className="p-4">
                  <h3 className="font-semibold text-lg mb-2 line-clamp-2">{product.name}</h3>
                  <div className="flex items-center justify-between mb-3">
                    <span className="text-2xl font-bold text-aloe-green">
                      {product.price.toLocaleString()} {product.currency}
                    </span>
                    <span className="text-xs px-2 py-1 bg-green-100 text-aloe-green rounded">
                      {product.category.split(' & ')[0]}
                    </span>
                  </div>
                  <p className="text-sm text-forever-gray-text mb-4 line-clamp-2">
                    {product.description}
                  </p>
                  <Link
                    to={`/catalogue?product=${product.id}`}
                    className="block w-full bg-aloe-green text-white text-center py-3 rounded-lg font-semibold hover:bg-aloe-green-dark transition-colors"
                  >
                    Voir le produit
                  </Link>
                </div>
              </div>
            ))}
          </div>
          
         <div className="text-center mt-12">
  <Link
    to="/catalogue"
    className="magic-button group relative inline-flex items-center justify-center px-8 py-3 rounded-lg font-semibold overflow-hidden transition-all duration-500 hover:scale-105"
  >
    {/* Effet de lumière derrière */}
