import React, { useState } from "react";
import { products } from "../../data/products";
import { useCart } from "../../context/CartContext";
import { Search, ShoppingCart, Star, TrendingUp, Zap, Shield } from "lucide-react";

const CataloguePage = () => {
  const [selectedCategory, setSelectedCategory] = useState("Tous les produits");
  const [searchTerm, setSearchTerm] = useState("");
  const { addToCart } = useCart();

  const categories = [
    "Tous les produits",
    "Soins & Beaut√©",
    "Boissons & Bien-√™tre",
    "Compl√©ments Alimentaires",
  ];

  const categoryIcons = {
    "Soins & Beaut√©": "üß¥",
    "Boissons & Bien-√™tre": "üçπ",
    "Compl√©ments Alimentaires": "üíä",
  };

  const categoryDescriptions = {
    "Soins & Beaut√©":
      "Des produits de soin √† l'Aloe Vera pour une peau saine et radieuse",
    "Boissons & Bien-√™tre":
      "Boissons naturelles √† l'Aloe Vera pour votre vitalit√© quotidienne",
    "Compl√©ments Alimentaires":
      "Suppl√©ments nutritionnels pour soutenir votre sant√© globale",
  };

  const formatPrice = (price) => `${price.toLocaleString()} FCFA`;

  const filteredProducts = products.filter((product) => {
    const matchesCategory =
      selectedCategory === "Tous les produits" ||
      product.category === selectedCategory;

    const matchesSearch =
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      product.description.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesCategory && matchesSearch;
  });

  // Fonction pour nettoyer les noms de fichiers (retirer les caract√®res sp√©ciaux)
  const cleanImageName = (name) => {
    return name.replace(/[¬Æ¬©]/g, '').trim();
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white py-10">
      <div className="container mx-auto px-4">
        {/* En-t√™te anim√© */}
        <div className="text-center mb-12 relative">
          <div className="absolute -top-10 -left-10 w-20 h-20 bg-gradient-to-r from-aloe-green/10 to-transparent rounded-full blur-2xl"></div>
          <div className="absolute -bottom-10 -right-10 w-20 h-20 bg-gradient-to-l from-aloe-gold/10 to-transparent rounded-full blur-2xl"></div>
          
          <h1 className="text-4xl md:text-5xl font-bold bg-gradient-to-r from-aloe-green via-aloe-green-dark to-aloe-green bg-clip-text text-transparent mb-4">
            Catalogue Forever Living
          </h1>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            D√©couvrez nos produits √† base d'Aloe Vera 100% naturel
          </p>
        </div>

        {/* Filtres am√©lior√©s */}
        <div className="mb-10 bg-white p-6 rounded-2xl shadow-lg border border-gray-100">
          <div className="flex flex-col md:flex-row gap-4 items-center">
            <div className="relative flex-1">
              <Search
                size={22}
                className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400"
              />
              <input
                type="text"
                placeholder="Rechercher un produit, un bienfait, une cat√©gorie..."
                className="w-full pl-12 pr-4 py-3 border-2 border-gray-200 rounded-xl focus:border-aloe-green focus:ring-2 focus:ring-aloe-green/20 outline-none transition-all"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* Filtres par cat√©gorie en boutons */}
            <div className="flex flex-wrap gap-2">
              {categories.map((cat) => (
                <button
                  key={cat}
                  onClick={() => setSelectedCategory(cat)}
                  className={`px-4 py-2 rounded-lg font-medium transition-all duration-300 ${
                    selectedCategory === cat
                      ? "bg-aloe-green text-white shadow-lg shadow-aloe-green/30"
                      : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                  }`}
                >
                  {categoryIcons[cat] || "üì¶"} {cat}
                </button>
              ))}
            </div>
          </div>

          {/* Description cat√©gorie */}
          {selectedCategory !== "Tous les produits" && (
            <div className="mt-6 p-4 bg-gradient-to-r from-aloe-green/5 to-aloe-gold/5 rounded-xl">
              <div className="flex items-center gap-3">
                <div className="text-2xl">{categoryIcons[selectedCategory]}</div>
                <div>
                  <h3 className="font-bold text-xl text-aloe-green-dark">
                    {selectedCategory}
                  </h3>
                  <p className="text-gray-600">
                    {categoryDescriptions[selectedCategory]}
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Compteur de r√©sultats */}
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 bg-aloe-green rounded-full animate-pulse"></div>
            <span className="text-lg font-semibold text-gray-700">
              {filteredProducts.length} produit{filteredProducts.length > 1 ? 's' : ''} trouv√©{filteredProducts.length > 1 ? 's' : ''}
            </span>
          </div>
          <div className="flex items-center gap-2 text-sm text-gray-500">
            <Shield size={16} />
            <span>Produits certifi√©s Forever Living</span>
          </div>
        </div>

        {/* Grille de produits am√©lior√©e */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {filteredProducts.map((product) => (
            <div
              key={product.id}
              className="group relative bg-white rounded-2xl overflow-hidden shadow-xl hover:shadow-2xl transition-all duration-500 hover:-translate-y-2"
            >
              {/* Effet de bordure lumineuse */}
              <div className="absolute inset-0 bg-gradient-to-br from-aloe-green/5 via-aloe-gold/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
              
              {/* Badge Best Seller */}
              {product.id <= 4 && (
                <div className="absolute top-4 left-4 z-10">
                  <div className="bg-gradient-to-r from-aloe-gold to-yellow-400 text-gray-900 px-3 py-1 rounded-full text-xs font-bold flex items-center gap-1 shadow-lg">
                    <Star size={10} fill="currentColor" />
                    <span>Best Seller</span>
                  </div>
                </div>
              )}

              {/* Section image am√©lior√©e */}
              <div className="relative h-64 bg-gradient-to-br from-gray-50 to-gray-100 overflow-hidden">
                <div className="absolute inset-0 flex items-center justify-center p-6">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="w-full h-full object-contain transform group-hover:scale-110 transition-transform duration-700 ease-out"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = `https://via.placeholder.com/400x400/2E7D32/FFFFFF?text=${encodeURIComponent(product.name)}`;
                    }}
                    loading="lazy"
                  />
                </div>
                
                {/* Overlay au survol */}
                <div className="absolute inset-0 bg-gradient-to-t from-black/20 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
              </div>

              {/* Infos produit */}
              <div className="p-6">
                {/* Cat√©gorie */}
                <div className="flex items-center justify-between mb-3">
                  <span className="inline-flex items-center gap-1 px-3 py-1 bg-gradient-to-r from-aloe-green/10 to-aloe-green/5 text-aloe-green-dark text-sm font-medium rounded-full">
                    {categoryIcons[product.category]} {product.category}
                  </span>
                  {product.stock < 5 && product.stock > 0 && (
                    <span className="text-xs font-bold text-orange-600 flex items-center gap-1">
                      <Zap size={10} />
                      Stock limit√©
                    </span>
                  )}
                </div>

                {/* Nom du produit */}
                <h3 className="font-bold text-xl text-gray-800 mb-2 line-clamp-2 group-hover:text-aloe-green-dark transition-colors">
                  {product.name}
                </h3>

                {/* Description */}
                <p className="text-gray-600 text-sm mb-4 line-clamp-2">
                  {product.description}
                </p>

                {/* Prix et stock */}
                <div className="flex items-center justify-between mb-4">
                  <div>
                    <p className="text-2xl font-bold text-aloe-green">
                      {formatPrice(product.price)}
                    </p>
                    <p className="text-xs text-gray-500">
                      Code: {product.code}
                    </p>
                  </div>
                  <div className={`text-sm font-semibold px-3 py-1 rounded-full ${
                    product.stock === 0
                      ? "bg-red-100 text-red-600"
                      : product.stock < 5
                      ? "bg-orange-100 text-orange-600"
                      : "bg-green-100 text-green-600"
                  }`}>
                    {product.stock === 0
                      ? "Rupture de stock"
                      : `${product.stock} en stock`}
                  </div>
                </div>

                {/* Bouton Ajouter au panier */}
                <button
                  onClick={() => addToCart(product)}
                  disabled={product.stock === 0}
                  className={`w-full py-3 px-4 rounded-xl font-semibold flex items-center justify-center gap-2 transition-all duration-300 ${
                    product.stock === 0
                      ? "bg-gray-200 text-gray-500 cursor-not-allowed"
                      : "bg-gradient-to-r from-aloe-green to-aloe-green-dark text-white hover:shadow-lg hover:shadow-aloe-green/30 hover:scale-[1.02]"
                  }`}
                >
                  <ShoppingCart size={20} />
                  {product.stock === 0 ? "Indisponible" : "Ajouter au panier"}
                </button>

                {/* Notes (√©toiles) */}
                <div className="flex items-center justify-center mt-4">
                  <div className="flex">
                    {[...Array(5)].map((_, i) => (
                      <Star
                        key={i}
                        size={14}
                        className="text-aloe-gold fill-current"
                      />
                    ))}
                  </div>
                  <span className="text-xs text-gray-500 ml-2">(4.8)</span>
                </div>
              </div>

              {/* Effet de lueur au survol */}
              <div className="absolute inset-0 border-2 border-transparent group-hover:border-aloe-green/20 rounded-2xl pointer-events-none transition-all duration-500"></div>
            </div>
          ))}
        </div>

        {/* √âtat vide */}
        {filteredProducts.length === 0 && (
          <div className="text-center py-20 bg-gradient-to-br from-white to-gray-50 rounded-3xl shadow-lg border border-gray-100">
            <div className="inline-block p-6 bg-gradient-to-r from-aloe-green/10 to-aloe-gold/10 rounded-full mb-6">
              <Search size={64} className="text-gray-400" />
            </div>
            <h3 className="text-2xl font-bold text-gray-700 mb-3">
              Aucun produit trouv√©
            </h3>
            <p className="text-gray-600 mb-8 max-w-md mx-auto">
              Essayez de modifier vos crit√®res de recherche ou choisissez une autre cat√©gorie
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button
                onClick={() => setSearchTerm("")}
                className="px-6 py-3 bg-gray-100 text-gray-700 rounded-lg font-semibold hover:bg-gray-200 transition-colors"
              >
                Effacer la recherche
              </button>
              <button
                onClick={() => setSelectedCategory("Tous les produits")}
                className="px-6 py-3 bg-gradient-to-r from-aloe-green to-aloe-green-dark text-white rounded-lg font-semibold hover:shadow-lg transition-all"
              >
                Voir tous les produits
              </button>
            </div>
          </div>
        )}

        {/* Section de garanties */}
        {filteredProducts.length > 0 && (
          <div className="mt-16 pt-10 border-t border-gray-200">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-gradient-to-br from-aloe-green/5 to-green-50 p-6 rounded-2xl border border-aloe-green/10">
                <div className="flex items-center gap-3 mb-4">
                  <div className="p-3 bg-aloe-green/10 rounded-lg">
                    <Shield className="text-aloe-green" size={24} />
                  </div>
                  <h4 className="font-bold text-lg text-gray-800">Qualit√© Certifi√©e</h4>
                </div>
                <p className="text-gray-600">
                  Tous nos produits Forever Living sont certifi√©s et garantis authentiques
                </p>
              </div>

              <div className="bg-gradient-to-br from-aloe-gold/5 to-yellow-50 p-6 rounded-2xl border border-aloe-gold/10">
                <div className="flex items-center gap-3 mb-4">
                  <div className="p-3 bg-aloe-gold/10 rounded-lg">
                    <Zap className="text-aloe-gold" size={24} />
                  </div>
                  <h4 className="font-bold text-lg text-gray-800">Livraison Express</h4>
                </div>
                <p className="text-gray-600">
                  Livraison rapide sous 48h √† Dakar et dans toutes les r√©gions du S√©n√©gal
                </p>
              </div>

              <div className="bg-gradient-to-br from-green-100/50 to-emerald-50 p-6 rounded-2xl border border-green-200">
                <div className="flex items-center gap-3 mb-4">
                  <div className="p-3 bg-green-100 rounded-lg">
                    <TrendingUp className="text-green-600" size={24} />
                  </div>
                  <h4 className="font-bold text-lg text-gray-800">Satisfaction Garantie</h4>
                </div>
                <p className="text-gray-600">
                  Retour gratuit sous 14 jours si vous n'√™tes pas satisfait de votre achat
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CataloguePage;
