import React, { useState } from 'react';
import { products } from '../../data/products'; // Import de base
import { useCart } from '../../context/CartContext';
import { Search, Filter, ShoppingCart } from 'lucide-react';

const CataloguePage = () => {
  const [selectedCategory, setSelectedCategory] = useState('Tous les produits');
  const [searchTerm, setSearchTerm] = useState('');
  const { addToCart } = useCart();

  // D√©finir localement si les exports sont manquants
  const categories = [
    "Tous les produits",
    "Soins & Beaut√©",
    "Boissons & Bien-√™tre",
    "Compl√©ments Alimentaires"
  ];

  const categoryIcons = {
    "Soins & Beaut√©": "üß¥",
    "Boissons & Bien-√™tre": "üçπ",
    "Compl√©ments Alimentaires": "üíä"
  };

  const categoryDescriptions = {
    "Soins & Beaut√©": "Des produits de soin √† l'Aloe Vera pour une peau saine et radieuse",
    "Boissons & Bien-√™tre": "Boissons naturelles √† l'Aloe Vera pour votre vitalit√© quotidienne",
    "Compl√©ments Alimentaires": "Suppl√©ments nutritionnels pour soutenir votre sant√© globale"
  };

  const formatPrice = (price) => {
    return `${price.toLocaleString()} FCFA`;
  };

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'Tous les produits' || product.category === selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4">
        <h1 className="text-3xl font-bold text-green-800 mb-8">Catalogue Forever Living</h1>

        {/* Filtres */}
        <div className="mb-8 bg-white p-4 rounded-lg shadow">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Rechercher un produit..."
                className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            
            <select
              className="border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              {categories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Description de cat√©gorie */}
        {selectedCategory !== 'Tous les produits' && (
          <div className="mb-8 p-6 bg-white rounded-xl shadow">
            <h2 className="text-2xl font-bold text-green-800 mb-2">
              {categoryIcons[selectedCategory]} {selectedCategory}
            </h2>
            <p className="text-gray-600">{categoryDescriptions[selectedCategory]}</p>
          </div>
        )}

        {/* Grille de produits */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredProducts.map(product => (
            <div key={product.id} className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow">
              <div className="h-48 bg-gray-100 flex items-center justify-center p-4">
                <img
                  src={product.image}
                  alt={product.name}
                  className="max-h-full max-w-full object-contain"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = `data:image/svg+xml;base64,${btoa(`
                      <svg width="400" height="300" xmlns="http://www.w3.org/2000/svg">
                        <rect width="400" height="300" fill="#f3f4f6"/>
                        <text x="200" y="150" font-family="Arial" font-size="16" text-anchor="middle" fill="#9ca3af">
                          ${product.name}
                        </text>
                      </svg>
                    `)}`;
                  }}
                />
              </div>
              
              <div className="p-4">
                <div className="flex justify-between items-start mb-2">
                  <h3 className="font-bold text-lg flex-1">{product.name}</h3>
                  <span className={`text-xs px-2 py-1 rounded-full ${
                    product.category === 'Soins & Beaut√©' ? 'bg-purple-100 text-purple-800' :
                    product.category === 'Boissons & Bien-√™tre' ? 'bg-blue-100 text-blue-800' :
                    'bg-orange-100 text-orange-800'
                  }`}>
                    {categoryIcons[product.category]} {product.category}
                  </span>
                </div>
                
                <p className="text-sm text-gray-600 mb-4 line-clamp-2">{product.description}</p>
                
                <div className="flex justify-between items-center mb-4">
                  <div>
                    <p className="text-2xl font-bold text-green-700">
                      {formatPrice(product.price)}
                    </p>
                    <p className="text-xs text-gray-500">Code: {product.code}</p>
                  </div>
                  
                  <div className="text-right">
                    <div className={`text-sm font-medium ${
                      product.stock === 0 ? 'text-red-600' :
                      product.stock < 5 ? 'text-orange-600' : 'text-green-600'
                    }`}>
                      {product.stock === 0 ? 'Rupture' : `${product.stock} en stock`}
                    </div>
                  </div>
                </div>

                <button
                  onClick={() => addToCart(product)}
                  disabled={product.stock === 0}
                  className={`w-full py-3 rounded-lg font-semibold flex items-center justify-center gap-2 ${
                    product.stock === 0
                      ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                      : 'bg-green-600 text-white hover:bg-green-700'
                  }`}
                >
                  <ShoppingCart size={18} />
                  {product.stock === 0 ? 'Rupture de stock' : 'Ajouter au panier'}
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Message si aucun produit */}
        {filteredProducts.length === 0 && (
          <div className="text-center py-12">
            <Search size={64} className="mx-auto text-gray-300 mb-4" />
            <h3 className="text-xl font-semibold text-gray-700 mb-2">Aucun produit trouv√©</h3>
            <p className="text-gray-600 mb-8">Essayez de modifier vos crit√®res de recherche</p>
            <button
              onClick={() => {
                setSelectedCategory('Tous les produits');
                setSearchTerm('');
              }}
              className="bg-green-600 text-white px-6 py-2 rounded-lg font-semibold hover:bg-green-700"
            >
              Voir tous les produits
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CataloguePage;
