import React from 'react';
import { Link } from 'react-router-dom';

const Banners = () => {
  const banners = [
    {
      id: 1,
      title: "Soins & Beauté",
      subtitle: "Découvrez notre gamme de soins à l'Aloe Vera",
      link: "/catalogue?category=Soins%20%26%20Beauté",
      color: "from-purple-600 to-purple-800",
      image: "https://images.unsplash.com/photo-1546069901-d5a64f8f2c4e?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80"
    },
    {
      id: 2,
      title: "Boissons & Bien-être",
      subtitle: "Pour une santé naturelle au quotidien",
      link: "/catalogue?category=Boissons%20%26%20Bien-être",
      color: "from-blue-600 to-blue-800",
      image: "https://images.unsplash.com/photo-1578500494198-246b612c5b9c?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80"
    }
  ];

  return (
    <div className="wrap-banner style-twin-default mb-12">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {banners.map((banner) => (
          <div key={banner.id} className="banner-item group">
            <Link 
              to={banner.link} 
              className="link-banner banner-effect-1 block overflow-hidden rounded-xl shadow-lg hover:shadow-2xl transition-all duration-300"
            >
              <div className={`h-48 md:h-64 bg-gradient-to-r ${banner.color} relative overflow-hidden`}>
                {/* Image de fond optionnelle - décommentez si vous voulez */}
                {/* <img
                  src={banner.image}
                  alt={banner.title}
                  className="absolute inset-0 w-full h-full object-cover opacity-20"
                /> */}
                
                <div className="absolute inset-0 flex flex-col items-center justify-center p-6 text-white">
                  <h3 className="text-2xl md:text-3xl font-bold mb-2 text-center">{banner.title}</h3>
                  <p className="text-white/80 text-center mb-4">{banner.subtitle}</p>
                  <span className="inline-block px-6 py-2 bg-white/20 backdrop-blur-sm rounded-lg font-semibold hover:bg-white/30 transition-colors">
                    Voir les produits
                  </span>
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Banners;
