import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import { Link } from 'react-router-dom';

const HeroSlider = () => {
  const slides = [
    {
      id: 1,
      title: "Découvrez la Puissance de l'Aloe Vera",
      subtitle: "Des produits naturels pour votre santé et bien-être",
      buttonText: "Acheter Maintenant",
      link: "/catalogue"
    },
    {
      id: 2,
      title: "Promotion Spéciale -25%",
      subtitle: "Sur tous les compléments alimentaires Forever",
      buttonText: "Profiter des Promotions",
      link: "/catalogue?promo=true"
    },
    {
      id: 3,
      title: "Nouvelle Collection",
      subtitle: "Découvrez nos nouveaux produits Forever Living",
      buttonText: "Découvrir",
      link: "/catalogue?new=true"
    }
  ];

  return (
    <div className="wrap-main-slide mb-8">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={0}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        autoplay={{ delay: 5000 }}
        loop={true}
        className="hero-slider"
      >
        {slides.map((slide) => (
          <SwiperSlide key={slide.id}>
            <div className="relative">
              {/* Image unique pour tous les slides */}
              <div className="h-[400px] md:h-[500px] lg:h-[600px] relative overflow-hidden">
                <img
                  src="/assets/images/hero.jpg"
                  alt={slide.title}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1600&q=80";
                  }}
                />
                {/* Overlay avec couleur différente par slide */}
                <div className={`absolute inset-0 bg-gradient-to-r ${
                  slide.id === 1 ? 'from-green-900/70 via-green-800/50 to-transparent' :
                  slide.id === 2 ? 'from-purple-900/70 via-purple-800/50 to-transparent' :
                  'from-blue-900/70 via-blue-800/50 to-transparent'
                }`}></div>
              </div>

              {/* Contenu du slide */}
              <div className="absolute inset-0 flex items-center">
                <div className="container mx-auto px-4">
                  <div className={`slide-info max-w-2xl ${slide.id === 2 ? 'text-center' : ''}`}>
                    <h2 className="f-title text-3xl md:text-5xl lg:text-6xl font-bold text-white mb-4 leading-tight">
                      {slide.title}
                    </h2>
                    
                    <span className="subtitle block text-lg md:text-xl lg:text-2xl text-white/90 mb-8">
                      {slide.subtitle}
                    </span>
                    
                    <Link
                      to={slide.link}
                      className={`btn-link inline-block px-6 py-3 md:px-8 md:py-4 rounded-lg font-bold text-lg text-white transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-1 ${
                        slide.id === 1 ? 'bg-aloe-green hover:bg-aloe-green-dark' :
                        slide.id === 2 ? 'bg-aloe-gold text-gray-800 hover:bg-yellow-500' :
                        'bg-blue-600 hover:bg-blue-700'
                      }`}
                    >
                      {slide.buttonText}
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default HeroSlider;
