import React from 'react';
import { Leaf, Shield, Users, Heart } from 'lucide-react';
import { motion } from 'framer-motion';

const fadeUp = {
  hidden: { opacity: 0, y: 30 },
  visible: { opacity: 1, y: 0 }
};

const AproposPage = () => {
  return (
    <div className="min-h-screen py-12 bg-gray-50 overflow-hidden">
      <div className="container mx-auto px-4">

        {/* Hero */}
        <motion.div
          className="text-center mb-16"
          initial="hidden"
          animate="visible"
          variants={fadeUp}
          transition={{ duration: 0.6 }}
        >
          <h1 className="text-4xl md:text-5xl font-bold text-green-800 mb-4">
            √Ä Propos de Nous
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Votre partenaire de confiance pour des produits naturels √† base d'Aloe Vera Forever Living
          </p>
        </motion.div>

        {/* Notre Histoire */}
        <motion.div
          className="bg-white rounded-xl shadow-lg p-8 mb-16"
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          variants={fadeUp}
          transition={{ duration: 0.6 }}
        >
          <div className="flex flex-col md:flex-row items-center">
            <div className="md:w-1/2 mb-8 md:mb-0">
              <h2 className="text-3xl font-bold text-green-800 mb-6">
                Notre Histoire
              </h2>
              <p className="text-gray-600 mb-4">
                Fond√©e en 2023, <strong>Vitalit√© & Bien-√™tre au Naturel</strong> est n√©e d'une passion pour les solutions de sant√© naturelles et durables.
              </p>
              <p className="text-gray-600 mb-4">
                Nous avons d√©couvert les bienfaits exceptionnels de l'Aloe Vera et avons choisi de nous associer avec <strong>Forever Living Products</strong>.
              </p>
              <p className="text-gray-600">
                Notre mission est de rendre ces produits accessibles √† tous au S√©n√©gal avec un accompagnement personnalis√©.
              </p>
            </div>

            <motion.div
              className="md:w-1/2 md:pl-12"
              initial={{ opacity: 0, scale: 0.95 }}
              whileInView={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
            >
              <img
                src="/assets/images/hero.jpg"
                alt="Aloe Vera Forever Living"
                className="rounded-xl shadow-xl object-cover"
              />
            </motion.div>
          </div>
        </motion.div>

        {/* Nos Valeurs */}
        <div className="mb-16">
          <motion.h2
            className="text-3xl font-bold text-center text-green-800 mb-12"
            initial="hidden"
            whileInView="visible"
            variants={fadeUp}
            viewport={{ once: true }}
          >
            Nos Valeurs
          </motion.h2>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {[
              { icon: Leaf, title: "Naturalit√©", text: "Produits 100% naturels sans additifs artificiels." },
              { icon: Shield, title: "Qualit√©", text: "Produits certifi√©s Forever Living." },
              { icon: Users, title: "Accompagnement", text: "Conseils personnalis√©s selon vos besoins." },
              { icon: Heart, title: "Bien-√™tre", text: "Votre sant√© est notre priorit√©." }
            ].map((item, index) => (
              <motion.div
                key={index}
                className="text-center p-6 bg-white rounded-xl shadow-md"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.15 }}
                viewport={{ once: true }}
                whileHover={{ y: -6 }}
              >
                <div className="bg-green-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                  <item.icon className="text-green-600" size={32} />
                </div>
                <h3 className="font-bold text-xl mb-3">{item.title}</h3>
                <p className="text-gray-600">{item.text}</p>
              </motion.div>
            ))}
          </div>
        </div>

        {/* Pourquoi Forever */}
        <motion.div
          className="bg-green-50 rounded-xl p-8 mb-16"
          initial="hidden"
          whileInView="visible"
          variants={fadeUp}
          viewport={{ once: true }}
        >
          <h2 className="text-3xl font-bold text-center text-green-800 mb-10">
            Pourquoi Forever Living ?
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {[
              ["üåø Culture Responsable", "Plantations durables sans pesticides."],
              ["‚öôÔ∏è Stabilisation √† froid", "Pr√©servation maximale des nutriments."],
              ["üèÜ Leader Mondial", "R√©f√©rence depuis 1978."]
            ].map((item, i) => (
              <motion.div
                key={i}
                className="bg-white p-6 rounded-xl shadow"
                whileHover={{ scale: 1.04 }}
              >
                <h3 className="font-bold text-lg mb-3">{item[0]}</h3>
                <p className="text-gray-600">{item[1]}</p>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Engagement */}
        <motion.div
          className="text-center bg-white rounded-xl shadow-lg p-8"
          initial="hidden"
          whileInView="visible"
          variants={fadeUp}
          viewport={{ once: true }}
        >
          <h2 className="text-3xl font-bold text-green-800 mb-6">
            Notre Engagement
          </h2>
          <p className="text-gray-600 text-lg max-w-3xl mx-auto mb-6">
            Nous nous engageons √† vous offrir des produits naturels de qualit√© et un service client d‚Äôexception.
          </p>
          <p className="text-gray-800 font-semibold">
            Rejoignez ceux qui ont choisi la naturalit√© pour une vie plus saine.
          </p>
        </motion.div>

      </div>
    </div>
  );
};

export default AproposPage;
