import React, { useState } from 'react';
import { 
  products, 
  categories, 
  categoryIcons, 
  categoryDescriptions, 
  formatPrice 
} from '../../data/products';
import { useCart } from '../../context/CartContext';
import { Search, Filter, ShoppingCart } from 'lucide-react';
const CataloguePage = () => {
  const [selectedCategory, setSelectedCategory] = useState('Tous les produits');
  const [searchTerm, setSearchTerm] = useState('');
  const { addToCart } = useCart();

  // Fonction pour obtenir une image avec fallback
  const getProductImage = (product) => {
    // Liste des images avec fallback vers les URLs Forever
    const imageMap = {
      "Forever Aloe First¬Æ Spray": "https://images.foreverliving.com/10605_front_1000px.jpg",
      "Aloe Heat Lotion": "https://images.foreverliving.com/10500_front_1000px.jpg",
      "Forever Aloe Liquid Soap¬Æ": "https://images.foreverliving.com/10602_front_1000px.jpg",
      "Forever Aloe Body Lotion¬Æ": "https://images.foreverliving.com/10598_front_1000px.jpg",
      "Forever Aloe Body Wash¬Æ": "https://images.foreverliving.com/10599_front_1000px.jpg",
      "Forever Aloe Lips¬Æ": "https://images.foreverliving.com/10594_front_1000px.jpg",
      "Forever Bright¬Æ Toothgel": "https://images.foreverliving.com/10604_front_1000px.jpg",
      "Forever Aloe Berry Nectar¬Æ": "https://images.foreverliving.com/10607_front_1000px.jpg",
      "Forever Freedom¬Æ": "https://images.foreverliving.com/10608_front_1000px.jpg",
      "Forever Vital 5¬Æ Aloe Gel": "https://images.foreverliving.com/10612_front_1000px.jpg",
      "Forever ARGI+¬Æ": "https://images.foreverliving.com/10013_front_1000px.jpg",
      "Forever Absorbent-C¬Æ": "https://images.foreverliving.com/10009_front_1000px.jpg",
      "Forever Daily¬Æ": "https://images.foreverliving.com/10019_front_1000px.jpg",
      "Forever Immublend¬Æ": "https://images.foreverliving.com/10022_front_1000px.jpg",
      "Forever Bee Propolis¬Æ": "https://images.foreverliving.com/10016_front_1000px.jpg"
    };
    
    // Retourne l'URL de fallback si l'image locale n'existe pas
    return imageMap[product.name] || product.image;
  };

  // Fonction pour cr√©er une image placeholder en SVG
  const createPlaceholderSVG = (productName) => {
    return `data:image/svg+xml;base64,${btoa(`
      <svg width="400" height="300" xmlns="http://www.w3.org/2000/svg">
        <rect width="400" height="300" fill="#f3f4f6"/>
        <rect width="400" height="200" fill="#e5e7eb"/>
        <text x="200" y="120" font-family="Arial" font-size="16" text-anchor="middle" fill="#666" font-weight="bold">
          ${productName}
        </text>
        <text x="200" y="150" font-family="Arial" font-size="14" text-anchor="middle" fill="#999">
          Forever Living
        </text>
        <text x="200" y="180" font-family="Arial" font-size="12" text-anchor="middle" fill="#2E7D32">
          Vitalit√© & Bien-√™tre
        </text>
        <rect x="150" y="220" width="100" height="30" rx="5" fill="#2E7D32"/>
        <text x="200" y="240" font-family="Arial" font-size="12" text-anchor="middle" fill="white">
          Aloe Vera
        </text>
      </svg>
    `)}`;
  };

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'Tous les produits' || product.category === selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const getCategoryColor = (category) => {
    switch(category) {
      case 'Soins & Beaut√©': return 'bg-purple-100 text-purple-800';
      case 'Boissons & Bien-√™tre': return 'bg-blue-100 text-blue-800';
      case 'Compl√©ments Alimentaires': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStockStatus = (stock) => {
    if (stock === 0) return { text: 'Rupture de stock', color: 'text-red-600' };
    if (stock < 5) return { text: `${stock} unit√©s restantes`, color: 'text-orange-600' };
    return { text: 'En stock', color: 'text-green-600' };
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section Catalogue */}
      <div className="bg-gradient-to-r from-aloe-green to-aloe-green-dark text-white py-12">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-5xl font-bold mb-4">Catalogue Forever Living</h1>
          <p className="text-xl opacity-90">D√©couvrez nos produits √† base d'Aloe Vera 100% naturel</p>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        {/* Filtres et recherche */}
        <div className="mb-8 bg-white p-6 rounded-xl shadow-lg">
          <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
            {/* Barre de recherche */}
            <div className="relative flex-1 max-w-lg">
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Rechercher un produit (nom, description)..."
                className="w-full pl-12 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-aloe-green focus:border-transparent"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* Filtre par cat√©gorie */}
            <div className="flex items-center gap-3">
              <Filter size={20} className="text-gray-500" />
              <select
                className="border border-gray-300 rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-aloe-green focus:border-transparent"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Description de cat√©gorie */}
        {selectedCategory !== 'Tous les produits' && (
          <div className="mb-8 p-6 bg-white rounded-xl shadow-lg">
            <h2 className="text-2xl font-bold text-aloe-green-dark mb-2">
              {categoryIcons[selectedCategory]} {selectedCategory}
            </h2>
            <p className="text-gray-600">{categoryDescriptions[selectedCategory]}</p>
          </div>
        )}

        {/* R√©sultats */}
        <div className="mb-6 flex justify-between items-center">
          <h2 className="text-2xl font-bold text-gray-800">
            {filteredProducts.length} produit{filteredProducts.length > 1 ? 's' : ''} trouv√©{filteredProducts.length > 1 ? 's' : ''}
          </h2>
          <div className="flex items-center gap-2 text-sm text-gray-600">
            <Truck size={16} />
            <span>Livraison sous 48h √† Dakar</span>
          </div>
        </div>

        {/* Grille de produits */}
        {filteredProducts.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {filteredProducts.map(product => {
              const stockStatus = getStockStatus(product.stock);
              
              return (
                <div key={product.id} className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-all duration-300">
                  {/* Image du produit */}
                  <div className="relative h-64 bg-gray-100 flex items-center justify-center p-4">
                    <img
                      src={getProductImage(product)}
                      alt={product.name}
                      className="max-h-full max-w-full object-contain transition-transform duration-300 hover:scale-105"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = createPlaceholderSVG(product.name);
                      }}
                    />
                    
                    {/* Badge cat√©gorie */}
                    <div className="absolute top-3 left-3">
                      <span className={`px-3 py-1 rounded-full text-xs font-medium ${getCategoryColor(product.category)}`}>
                        {categoryIcons[product.category] || 'üì¶'} {product.category.split('&')[0].trim()}
                      </span>
                    </div>
                    
                    {/* Badge stock limit√© */}
                    {product.stock < 5 && product.stock > 0 && (
                      <span className="absolute top-3 right-3 px-3 py-1 rounded-full text-xs font-bold bg-aloe-gold text-gray-800">
                        ‚ö° Stock limit√©
                      </span>
                    )}
                  </div>
                  
                  {/* Informations produit */}
                  <div className="p-5">
                    <div className="mb-3">
                      <span className={`px-3 py-1 rounded-full text-xs font-medium ${getCategoryColor(product.category)}`}>
                        {product.category}
                      </span>
                    </div>
                    
                    <h3 className="font-bold text-lg text-gray-800 mb-2 line-clamp-2">
                      {product.name}
                    </h3>
                    
                    <p className="text-sm text-gray-600 mb-4 line-clamp-2">
                      {product.description}
                    </p>
                    
                    {/* Prix et stock */}
                    <div className="mb-4">
                      <div className="flex justify-between items-center mb-2">
                        <div>
                          <p className="text-2xl font-bold text-aloe-green">
                            {formatPrice(product.price)}
                          </p>
                          <p className="text-xs text-gray-500">Code: {product.code}</p>
                        </div>
                        <div className={`text-sm font-medium ${stockStatus.color}`}>
                          {stockStatus.text}
                        </div>
                      </div>
                      
                      {/* √âtoiles */}
                      <div className="flex items-center mb-2">
                        <div className="flex">
                          {[...Array(5)].map((_, i) => (
                            <Star key={i} size={14} className="text-aloe-gold fill-current" />
                          ))}
                        </div>
                        <span className="text-xs text-gray-500 ml-2">(4.8)</span>
                      </div>
                    </div>
                    
                    {/* Bouton d'action */}
                    <button
                      onClick={() => addToCart(product)}
                      disabled={product.stock === 0}
                      className={`w-full py-3 rounded-lg font-semibold transition-all duration-300 flex items-center justify-center gap-2 ${
                        product.stock === 0
                          ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                          : 'bg-aloe-green text-white hover:bg-aloe-green-dark hover:shadow-lg'
                      }`}
                    >
                      {product.stock === 0 ? (
                        'Rupture de stock'
                      ) : (
                        <>
                          <ShoppingCart size={18} />
                          <span>Ajouter au panier</span>
                        </>
                      )}
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          /* √âtat vide */
          <div className="text-center py-16 bg-white rounded-xl shadow-lg">
            <Search size={64} className="mx-auto text-gray-300 mb-4" />
            <h3 className="text-xl font-semibold text-gray-700 mb-2">Aucun produit trouv√©</h3>
            <p className="text-gray-600 mb-6">Essayez de modifier vos crit√®res de recherche</p>
            <button
              onClick={() => {
                setSelectedCategory('Tous les produits');
                setSearchTerm('');
              }}
              className="bg-aloe-green text-white px-6 py-3 rounded-lg font-semibold hover:bg-aloe-green-dark transition-colors"
            >
              Voir tous les produits
            </button>
          </div>
        )}

        {/* Avantages Forever */}
        <div className="mt-16 pt-8 border-t border-gray-200">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-shadow">
              <div className="w-12 h-12 bg-aloe-green rounded-full flex items-center justify-center mx-auto mb-4">
                <Leaf className="text-white" size={24} />
              </div>
              <h3 className="font-bold text-xl mb-2 text-center">Aloe Vera 100% Naturel</h3>
              <p className="text-gray-600 text-center">Nos produits sont fabriqu√©s √† partir d'Aloe Vera pur, r√©colt√© et stabilis√© √† froid.</p>
            </div>
            
            <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-shadow">
              <div className="w-12 h-12 bg-aloe-green rounded-full flex items-center justify-center mx-auto mb-4">
                <Shield className="text-white" size={24} />
              </div>
              <h3 className="font-bold text-xl mb-2 text-center">Qualit√© Certifi√©e</h3>
              <p className="text-gray-600 text-center">Tous nos produits sont certifi√©s par les normes internationales les plus strictes.</p>
            </div>
            
            <div className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg transition-shadow">
              <div className="w-12 h-12 bg-aloe-green rounded-full flex items-center justify-center mx-auto mb-4">
                <Truck className="text-white" size={24} />
              </div>
              <h3 className="font-bold text-xl mb-2 text-center">Livraison Rapide</h3>
              <p className="text-gray-600 text-center">Commandez avant 16h et recevez votre colis sous 48h √† Dakar.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CataloguePage;
